// Configuration - Update these values
const brandName = "YourBrandName"; // TODO: Replace with your actual brand name

// TODO: Replace these example queries with ones relevant to YOUR brand and industry.
// Aim for a mix of query types to get comprehensive insights.
const queries = [
  // --- General Brand Awareness & Perception ---
  "What is [YourBrandName]?",
  "Tell me about [YourBrandName]'s main products/services.",
  "What are the benefits of using [YourBrandName]?",
  "How does [YourBrandName] compare to other solutions in the [YourIndustry] market?",
  "What do people say about [YourBrandName]?",

  // --- Problem/Solution Fit (User trying to solve a problem YourBrandName addresses) ---
  "How can I solve [Problem YourBrandName Solves]?",
  "What are the best tools for [Task YourBrandName Helps With]?",
  "I'm looking for a way to [Achieve Goal YourBrandName Facilitates], any suggestions?",

  // --- Feature-Specific or Use-Case Queries ---
  "Does [YourBrandName] offer [Specific Feature]?",
  "How can I use [YourBrandName] for [Specific Use Case]?",

  // --- Competitor Comparison (Direct & Indirect) ---
  // Replace [Competitor A/B/C] with actual competitor names relevant to you.
  "What are alternatives to [Competitor A]?",
  "Compare [YourBrandName] and [Competitor B].",
  "Is [YourBrandName] better than [Competitor C] for [Specific Need]?",

  // --- Industry or Category Questions (Where YourBrandName should ideally be mentioned) ---
  "What are the leading companies in the [YourIndustry] sector?",
  "Recommend a good [Product/Service Category YourBrandName Belongs To].",

  // --- Negative or Challenging Queries (To see how AI handles them) ---
  "What are some downsides of using [YourBrandName]?",
  "Are there any common complaints about [YourBrandName]?",

  // --- Buying Intent / Recommendation Seeking ---
  "Should I choose [YourBrandName] for [Specific Purpose]?",
  "What's the best [Product/Service Category] for someone who needs [Specific Requirement]?",

  // --- Add more queries specific to your brand, product features, target audience pain points, and competitive landscape ---
  // Example: "How does [YourBrandName] handle [Unique Selling Proposition]?"
  // Example: "What are the pricing options for [YourBrandName]?"
];

// Create output items
const items = [];
for (let i = 0; i < queries.length; i++) {
  items.push({
    json: {
      brandName: brandName,
      query: queries[i],
      queryIndex: i + 1,
      timestamp: new Date().toISOString(),
      date: new Date().toISOString().split('T')[0]
    }
  });
}

return items;

// Process the response and check for brand mentions
const httpResponse = $json; // This is the HTTP response
const originalQueries = $('Setup Queries').all(); // Get all original queries

// Find the matching original query for this response
// Since we're processing items one by one, we need to match by index
const currentIndex = $itemIndex; // Current item index
const originalData = originalQueries[currentIndex]?.json;

// Get brand name from original data
const brandName = originalData?.brandName?.toLowerCase() || 'unknown';

let response = '';
let brandMentioned = 'No';
let error = null;

// Check if there was an error
if (httpResponse.statusCode && httpResponse.statusCode !== 200) {
  response = `HTTP Error: ${httpResponse.statusCode} - ${httpResponse.statusMessage || 'Request failed'}`;
  brandMentioned = 'Error';
  error = `HTTP ${httpResponse.statusCode}`;
} else if (httpResponse.body && httpResponse.body.choices && httpResponse.body.choices[0]) {
  response = httpResponse.body.choices[0].message.content;
  // Check for brand mention (case insensitive)
  if (brandName && brandName !== 'unknown' && response.toLowerCase().includes(brandName)) {
    brandMentioned = 'Yes';
  }
} else if (httpResponse.body && httpResponse.body.error) {
  response = `API Error: ${httpResponse.body.error.message}`;
  brandMentioned = 'Error';
  error = httpResponse.body.error.message;
} else {
  response = 'No valid response received';
  brandMentioned = 'Error';
  error = 'Invalid response structure';
}

// Return combined data
return {
  json: {
    timestamp: originalData?.timestamp || new Date().toISOString(),
    date: originalData?.date || new Date().toISOString().split('T')[0],
    query: originalData?.query || 'Unknown query',
    queryIndex: originalData?.queryIndex || currentIndex + 1,
    brandName: originalData?.brandName || 'Unknown brand',
    response: response.substring(0, 500), // Limit response length
    brandMentioned: brandMentioned,
    error: error
  }
};

// Generate email summary with competitor detection
const allItems = $input.all();

// Get the processed data from Process Response node
const processedData = $('Process Response').all();
const dataToUse = processedData.length > 0 ? processedData : allItems;

// Define competitors to look for (add/modify as needed)
const competitors = [
  'honey',
  'rakuten',
  'ebates',
  'capital one shopping',
  'wikibuy',
  'retailmenot',
  'couponfollow',
  'groupon',
  'slickdeals',
  'cashback',
  'ibotta',
  'dosh',
  'drop',
  'pei',
  'checkout 51',
  'swagbucks',
  'topcashback',
  'mr. rebates',
  'befrugal',
  'extrabux'
];

// Function to find competitors mentioned in response
function findCompetitors(response) {
  if (!response || typeof response !== 'string') return [];

  const responseText = response.toLowerCase();
  const foundCompetitors = [];

  competitors.forEach(competitor => {
    if (responseText.includes(competitor.toLowerCase())) {
      // Capitalize first letter of each word for display
      const displayName = competitor.split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ');
      foundCompetitors.push(displayName);
    }
  });

  return foundCompetitors;
}

const totalQueries = dataToUse.length;
const mentions = dataToUse.filter(item => item.json.brandMentioned === 'Yes').length;
const errors = dataToUse.filter(item => item.json.brandMentioned === 'Error').length;
const brandName = dataToUse[0]?.json.brandName || 'Unknown';
const date = dataToUse[0]?.json.date || new Date().toISOString().split('T')[0];

// Count total competitor mentions
let totalCompetitorMentions = 0;
const competitorCounts = {};

// Create HTML table
let tableRows = '';
dataToUse.forEach((item, index) => {
  const bgColor = item.json.brandMentioned === 'Yes' ? '#d4edda' : 
                  item.json.brandMentioned === 'Error' ? '#f8d7da' : '#fff';

  // Find competitors in this response
  const competitorsFound = findCompetitors(item.json.response);
  const competitorsDisplay = competitorsFound.length > 0 ? 
    competitorsFound.join(', ') : 
    'None';

  // Count competitors for summary
  competitorsFound.forEach(comp => {
    competitorCounts[comp] = (competitorCounts[comp] || 0) + 1;
    totalCompetitorMentions++;
  });

  tableRows += `
    <tr style="background-color: ${bgColor};">
      <td>${item.json.queryIndex || index + 1}</td>
      <td>${item.json.query || 'Unknown query'}</td>
      <td><strong>${item.json.brandMentioned || 'Unknown'}</strong></td>
      <td>${competitorsDisplay}</td>
    </tr>
  `;
});

// Create competitor summary
let competitorSummary = '';
if (Object.keys(competitorCounts).length > 0) {
  competitorSummary = '<h3>Competitor Mentions Summary:</h3><ul>';
  Object.entries(competitorCounts)
    .sort((a, b) => b[1] - a[1]) // Sort by count descending
    .forEach(([competitor, count]) => {
      competitorSummary += `<li><strong>${competitor}</strong>: ${count} mentions</li>`;
    });
  competitorSummary += '</ul>';
} else {
  competitorSummary = '<p><em>No competitors mentioned in any responses.</em></p>';
}

const subject = mentions > 0 ? 
  `ðŸŽ¯ Brand Mentioned! ${brandName} found in ${mentions}/${totalQueries} queries` :
  `ðŸ“Š Daily Report: ${brandName} - No mentions found`;

const htmlContent = `
<h2>Brand Mention Monitor Report</h2>
<p><strong>Brand:</strong> ${brandName}</p>
<p><strong>Date:</strong> ${date}</p>
<p><strong>Summary:</strong></p>
<ul>
  <li>Total Queries: ${totalQueries}</li>
  <li>Brand Mentions: ${mentions}</li>
  <li>Competitor Mentions: ${totalCompetitorMentions}</li>
  <li>Errors: ${errors}</li>
  <li>Success Rate: ${Math.round(((totalQueries - errors) / totalQueries) * 100)}%</li>
</ul>

${competitorSummary}

<table border="1" style="border-collapse: collapse; width: 100%;">
  <thead>
    <tr style="background-color: #f8f9fa;">
      <th>Query #</th>
      <th>Query</th>
      <th>Brand Mentioned</th>
      <th>Competitors Mentioned</th>
    </tr>
  </thead>
  <tbody>
    ${tableRows}
  </tbody>
</table>

<p><em>Full details saved to Google Sheets</em></p>
`;

return {
  json: {
    subject: subject,
    htmlContent: htmlContent,
    summary: {
      totalQueries,
      mentions,
      errors,
      brandName,
      date,
      competitorMentions: totalCompetitorMentions,
      competitorBreakdown: competitorCounts
    }
  }
};
