function formatPatch(patch) {
  if (!patch) return '';

  const lines = patch.split('\n');
  let formatted = '';

  lines.forEach(line => {
    let color = '#000000'; // default text
    if (line.startsWith('+')) {
      color = '#237804'; // green
    } else if (line.startsWith('-')) {
      color = '#cf1322'; // red
    } else if (line.startsWith('@@')) {
      color = '#595959'; // gray for hunk headers
    }

    formatted += `<div style="color: ${color}; font-family: monospace;">${line.replace(/</g, '&lt;').replace(/>/g, '&gt;')}</div>`;
  });

  return formatted;
}

function renderResponse(data, repoDetails) {
  const items = Array.isArray(data) ? data : [data];

  let html = ``;

  // Repo Info
  html += `
    <div style="margin: 20px 10px; padding: 15px; background-color: #f0f9ff; border-left: 5px solid #1890ff; font-family: Arial, sans-serif;">
      <h2 style="margin-top: 0; color: #0050b3;">üîó Repository Info</h2>
      <p style="margin: 5px 0;"><strong>Name:</strong> <a href="${repoDetails.repo_url}" target="_blank" style="color: #007acc;">${repoDetails.name}</a></p>
      <p style="margin: 5px 0;"><strong>Owner:</strong> ${repoDetails.owner_name}</p>
    </div>
  `;

  // Commit Info
  html += `
    <div style="margin: 20px 10px; padding: 15px; background-color: #e6f7ff; border-left: 5px solid #52c41a; font-family: Arial, sans-serif;">
      <h3 style="margin-top: 0; color: #237804;">‚úÖ Commit Info</h3>
      <p style="margin: 5px 0;"><strong>Author:</strong> ${repoDetails.author.name}</p>
      <p style="margin: 5px 0;"><strong>Email:</strong> ${repoDetails.author.email}</p>
      <p style="margin: 5px 0;"><strong>Date:</strong> ${repoDetails.author.date}</p>
      <p style="margin: 5px 0;"><strong>Message:</strong> ${repoDetails.commit_message}</p>
    </div>
  `;

  // Changed files
  items.forEach(item => {
    html += `
      <section style="border: 1px solid #d9d9d9; margin: 10px; padding: 15px; border-radius: 8px; background-color: #ffffff; font-family: Arial, sans-serif;">
        <h3 style="margin-top: 0; color: #262626;">üìÑ File: ${item.filename}</h3>
        <p><strong>Status:</strong> <span style="color: #fa8c16;">${item.status}</span></p>
        <p><strong>SHA:</strong> <code style="background-color: #f5f5f5; padding: 2px 6px; border-radius: 3px;">${item.sha}</code></p>
        <p>
          <strong>‚ûï Additions:</strong> ${item.additions}, 
          <strong>‚ûñ Deletions:</strong> ${item.deletions}, 
          <strong>‚úèÔ∏è Changes:</strong> ${item.changes}
        </p>
        <div style="background-color: #f6f6f6; padding: 10px; overflow-x: auto; border-radius: 5px; font-size: 13px; line-height: 1.4; border-left: 3px solid #d9d9d9;">
          ${formatPatch(item.patch)}
        </div>
      </section>
    `;
  });

  return [{
    json: {
      htmlOutput: html
    }
  }];
}

