const views = $json.viewCount;
const subscribers = $json.numberOfSubscribers;
const likes = $json.likes;
const comments = $json.commentsCount;
const publishedAt = $json.date;// ISO string, e.g. "2024-05-01T12:00:00Z"

// Defensive check for missing values
if (!views || !subscribers || !publishedAt) {
  return { error: "Missing required data" };
}

// Parse dates
const publishedDate = new Date(publishedAt);
const now = new Date();

// Validate dates
if (isNaN(publishedDate.getTime())) {
  return { error: "Invalid publishedAt date" };
}

// Calculate days since upload
const msPerDay = 1000 * 60 * 60 * 24;
const timeDiff = now.getTime() - publishedDate.getTime();
const daysSinceUpload = Math.floor(timeDiff / msPerDay);

// Compute RPI
const rpi = (views / subscribers) * ((likes + comments) / views / 10);

// Apply temporal decay factor
const decayFactor = Math.exp(-0.015 * daysSinceUpload);
const rpiAdjusted = rpi * decayFactor;

// Calculate Pseudo CTR
const ctrScore = rpiAdjusted * 100;

return {
   ctrPercent: parseFloat(ctrScore.toFixed(3)), // ‚Üê Your CTR-style %
};

