// 'items' is already defined in n8n and contains incoming data

return items.map(item => {
  const link = item.json.link; // e.g., "https://www.youtube.com/watch?v=Y0DYmRnfHlc"
  const videoId = link.split("v=")[1].split("&")[0];
  return {
    json: {
      videoId,
    },
  };
});

