for (const item of $input.all()) {
  let isValidUrl = false; // Start with false, will be set to true if passes checks
  let errorMessage = '';
  let finalUrl = null; // This will store the validated/resolved URL

  const headResponse = item.json; // Assuming the HTTP Request node outputs directly to item.json

  // Check if the HEAD request was successful (status 2xx or 3xx for redirects)
  const statusCode = headResponse.statusCode;
  console.log('DEBUG: HEAD Request Status Code:', statusCode);
  console.log('DEBUG: HEAD Request Headers:', JSON.stringify(headResponse.headers));

  if (statusCode >= 200 && statusCode < 400) {
    // Get the final URL after redirects, or original URL if no redirect
    let resolvedUrlString = headResponse.headers?.['x-final-url'] || headResponse.url || headResponse.request?.url;

    console.log('DEBUG: Resolved URL string from HEAD request:', resolvedUrlString);

    if (resolvedUrlString) {
        finalUrl = resolvedUrlString;

        // Manual parsing of protocol and hostname from resolvedUrlString
        let protocol = '';
        let hostname = '';

        const protocolMatch = finalUrl.match(/^(\w+):\/\//);
        if (protocolMatch && protocolMatch[1]) {
            protocol = protocolMatch[1];
            // Remove protocol and leading slashes for hostname extraction
            let tempUrl = finalUrl.substring(protocol.length + 3); // e.g., "https://www.example.com/path" -> "www.example.com/path"
            const slashIndex = tempUrl.indexOf('/');
            if (slashIndex !== -1) {
                hostname = tempUrl.substring(0, slashIndex); // e.g., "www.example.com"
            } else {
                hostname = tempUrl; // e.g., "www.example.com" (if no path)
            }
        }
        console.log('DEBUG: Manually parsed Protocol:', protocol, 'Hostname:', hostname);

        // Basic protocol check
        if (protocol === 'http' || protocol === 'https') {
            isValidUrl = true; // Protocol is good so far
        } else {
            isValidUrl = false;
            errorMessage = 'Only HTTP or HTTPS protocols are allowed.';
            console.log('ERROR: Protocol check failed. Detected protocol:', protocol);
        }

        // Check for private IPs or localhost to prevent SSRF (only if valid so far)
        if (isValidUrl && hostname) { // Ensure hostname was extracted
            if (hostname === 'localhost' || hostname === '127.0.0.1') {
                isValidUrl = false;
                errorMessage = 'Access to localhost is not allowed for security reasons.';
                console.log('ERROR: Localhost or 127.0.0.1 detected.');
            } else {
                const parts = hostname.split('.');
                // Check if it's an IPv4 address (4 parts) and all parts are numbers
                if (parts.length === 4 && parts.every(part => !isNaN(parseInt(part)) && isFinite(part) && parseInt(part) >= 0 && parseInt(part) <= 255)) {
                    const ip = parts.map(Number);
                    if (ip[0] === 10 || (ip[0] === 172 && ip[1] >= 16 && ip[1] <= 31) || (ip[0] === 192 && ip[1] === 168)) {
                        isValidUrl = false;
                        errorMessage = 'Access to private IP addresses is not allowed for security reasons.';
                        console.log('ERROR: Private IPv4 address detected:', ip);
                    }
                }
            }
        } else if (!hostname) { // If hostname couldn't be extracted, it's not valid
             isValidUrl = false;
             errorMessage = 'Could not determine hostname from URL.';
             console.log('ERROR: Hostname could not be extracted.');
        }

    } else { // No resolved URL string from HEAD request
        isValidUrl = false;
        errorMessage = 'Failed to get a resolved URL string from HEAD request response.';
        console.log('ERROR: No resolved URL string found in HEAD request output.');
    }
  } else { // HEAD request returned non-2xx/3xx status
    isValidUrl = false;
    errorMessage = `URL is unreachable or returned status code ${statusCode || 'unknown'}.`;
    console.log(`ERROR: HEAD request failed with status code: ${statusCode}`);
  }

  // Add validation status to the item's JSON
  item.json.isValidUrl = isValidUrl;
  item.json.errorMessage = errorMessage;
  item.json.validatedUrl = isValidUrl ? finalUrl : null;
  item.json.statusCode = statusCode; // Add status code for debug from HEAD request
  console.log('DEBUG: Final isValidUrl for this item:', isValidUrl, 'Final errorMessage:', errorMessage);
}

return $input.all();
