const country = items[0].json.city?.country || 'LU';
const timezone = items[0].json.city?.timezone || 0;

const now = new Date();
const today = now.toISOString().split('T')[0];
const desiredHours = ["09:00", "12:00", "15:00", "18:00", "21:00"];

let summary = '';
let headerIcon = 'ğŸŒ¤ï¸';
let dayHighTemp = -999;
let dayLowTemp = 999;
let totalRain = 0;
let avgHumidity = 0;
let avgWindSpeed = 0;
let weatherCount = 0;

// Process today's forecast
let firstMatch = true;
forecast.forEach(item => {
 const dt = item.dt_txt;
 const [date, time] = dt.split(' ');
 const hour = time.slice(0, 5);
 const hourNum = parseInt(time.slice(0, 2));

 if (date === today && desiredHours.includes(hour)) {
   const temp = Math.round(item.main.temp);
   const feelsLike = Math.round(item.main.feels_like);
   const humidity = item.main.humidity;
   const windSpeed = item.wind?.speed || 0;
   const rain = item.rain?.['3h'] || 0;
   const clouds = item.clouds?.all || 0;
   const weatherMain = item.weather?.[0]?.main || '';
   const weatherDesc = item.weather?.[0]?.description || '';
   const isNight = hourNum < 6 || hourNum > 20;

   // Track daily stats
   dayHighTemp = Math.max(dayHighTemp, temp);
   dayLowTemp = Math.min(dayLowTemp, temp);
   totalRain += rain;
   avgHumidity += humidity;
   avgWindSpeed += windSpeed;
   weatherCount++;

   // Set header icon based on first matching hour
   if (firstMatch && weatherMain) {
     headerIcon = getWeatherEmoji(weatherMain, clouds, isNight);
     firstMatch = false;
   }

   const tempEmoji = getTemperatureEmoji(temp);
   const weatherEmoji = getWeatherEmoji(weatherMain, clouds, isNight);
   const feelsLikeText = Math.abs(temp - feelsLike) > 2 ? ` (feels ${feelsLike}Â°C)` : '';
   const rainText = rain > 0 ? `, ğŸŒ§ï¸ ${rain}mm` : '';
   const windText = windSpeed > 3 ? `, ${getWindInfo(windSpeed).split(' ')[0]} ${(windSpeed * 3.6).toFixed(0)} km/h` : '';
   const humidityText = humidity > 80 || humidity < 30 ? `, ğŸ’§ ${humidity}%` : '';
   const uvText = getUVAdvice(hourNum);

   summary += `ğŸ•’ <b>${hour}</b> ${weatherEmoji} ${temp}Â°C${feelsLikeText}${rainText}${windText}${humidityText}\n`;
   if (uvText) summary += `    ${uvText}\n`;
 }
});

// Calculate averages
if (weatherCount > 0) {
 avgHumidity = Math.round(avgHumidity / weatherCount);
 avgWindSpeed = avgWindSpeed / weatherCount;
}

// Build comprehensive message
let message = `${headerIcon} <b>Weather Forecast for ${cityName}, ${country}</b>\n`;
message += `ğŸ“… ${now.toLocaleDateString('en-GB', { 
 weekday: 'long', 
 year: 'numeric', 
 month: 'long', 
 day: 'numeric',
 timeZone: 'Europe/Luxembourg'
})}\n\n`;

// Daily overview
message += `<b>ğŸ“Š Daily Overview</b>\n`;
if (dayHighTemp > -999 && dayLowTemp < 999) {
 message += `ğŸŒ¡ï¸ Range: ${dayLowTemp}Â°C - ${dayHighTemp}Â°C\n`;
}
if (totalRain > 0) {
 message += `ğŸŒ§ï¸ Expected rain: ${totalRain.toFixed(1)}mm\n`;
}
if (avgWindSpeed > 2) {
 message += `${getWindInfo(avgWindSpeed)}\n`;
}
message += `${getHumidityLevel(avgHumidity)} (${avgHumidity}%)\n\n`;

// Hourly forecast
message += `<b>â° Hourly Forecast</b>\n`;
message += summary;

// Smart recommendations - only show section if there are recommendations
let recommendations = '';
if (totalRain > 0) {
 recommendations += `â˜‚ï¸ Bring an umbrella or raincoat\n`;
}
if (dayLowTemp < 10) {
 recommendations += `ğŸ§¥ Dress warmly (below 10Â°C expected)\n`;
}
if (dayHighTemp > 25) {
 recommendations += `ğŸ’§ Stay hydrated and consider sun protection\n`;
}
if (avgWindSpeed > 15) {
 recommendations += `ğŸ’¨ Windy conditions - secure loose items\n`;
}
if (avgHumidity > 80) {
 recommendations += `ğŸ’¦ High humidity - may feel warmer than actual temperature\n`;
}

// Only add recommendations section if there are any recommendations
if (recommendations) {
 message += `\n<b>ğŸ’¡ Recommendations</b>\n`;
 message += recommendations;
}

// Footer with data source and time
const updateTime = now.toLocaleTimeString('en-GB', {
 hour: '2-digit',
 minute: '2-digit',
 timeZone: 'Europe/Luxembourg'
});
message += `\n<i>ğŸ“¡ Data from OpenWeatherMap | Updated: ${updateTime} CET</i>`;

