const inputData = items[0].json;

const centerLatitude = parseFloat(inputData.Latitude);
const centerLongitude = parseFloat(inputData.Longitude);
const radius = 200; // Radius in meters (adjust as needed) - Consider making this an input parameter
const numPoints = 20; // Number of points on the circle

const earthRadius = 6371000; // Earth's radius in meters

let results = [];


for (let i = 0; i < numPoints; i++) {
    const angle = 2 * Math.PI * i / numPoints;
    const lat = centerLatitude + (radius / earthRadius) * (180 / Math.PI) * Math.cos(angle);
    const lon = centerLongitude + (radius / earthRadius) * (180 / Math.PI) * Math.sin(angle)  / Math.cos(centerLatitude * Math.PI / 180);
    results.push({
        ...inputData,
        latitude: lat,
        longitude: lon
    })
}


// Create an object with a key called "result" to hold the results array
// this ensure that the the json key points to a valid object in item[0]
items[0].json.result = results

//return the updated item
return items;

const today = new Date();
today.setUTCHours(0, 0, 0, 0);  // Ensures time is set to 00:00:00.000 UTC
const activeFrom = today.toISOString();  // Ensures the string format

const futureDate = new Date(today);
futureDate.setDate(today.getDate() + 60);
futureDate.setUTCHours(0, 0, 0, 0);  // Ensures time is set to 00:00:00.000 UTC
const activeTo = futureDate.toISOString();  // Ensures the string format

return [
    {
        json: {
            activeFrom: activeFrom.split(".")[0] + "Z",  // Removes milliseconds if necessary
            activeTo: activeTo.split(".")[0] + "Z"  // Removes milliseconds if necessary
        }
    }
];

