// This node validates if the 'jsonString' in the webhook body is valid JSON.
// It returns 'valid: true' or 'valid: false' along with an error message if invalid.

const results = [];

for (const item of $input.all()) {
  try {
    // Attempt to parse the jsonString from the webhook body
    // Ensure 'jsonString' exists before attempting to parse
    if (item.json.body && typeof item.json.body.jsonString === 'string') {
      JSON.parse(item.json.body.jsonString);
      results.push({ json: { valid: true } });
    } else {
      // Handle cases where jsonString is missing or not a string
      results.push({ json: { valid: false, error: "Input 'jsonString' is missing or not a string." } });
    }
  } catch (e) {
    // If parsing fails, push the error message
    results.push({ json: { valid: false, error: e.message } });
  }
}

return results;
