parameters.jsCode:
// --- Corrected Script ---

console.log("--- 1. SCRIPT INITIALIZING ---");
const cheerio = require('cheerio');
console.log("Cheerio library loaded.");

const fullHtml = $('Get Website HTML').first().json.data;
const waveCategories = $('Get WAVE Report').first().json.categories;
console.log(`Input data loaded. HTML Length: ${fullHtml.length}`);

// --- 3. INITIALIZING CHEERIO ---
const $$ = cheerio.load(fullHtml);
console.log(`Cheerio loaded HTML successfully. Document title: "${$$('title').text()}"`);

const analysisItems = [];

// --- 4. PROCESSING LOOP ---
if (waveCategories && typeof waveCategories === 'object') {
  for (const categoryName in waveCategories) {
    const category = waveCategories[categoryName];
    if (category && category.items && typeof category.items === 'object') {
      for (const itemId in category.items) {
        const item = category.items[itemId];
        if (item.selectors && Array.isArray(item.selectors)) {

          // Loop through the array of selector strings
          for (const selectorString of item.selectors) {

            // --- THIS IS THE CORRECTED LINE ---
            // The selector is the string itself from the array.
            const selector = selectorString;

            // Ensure the selector is a valid string before using it
            if (typeof selector === 'string' && selector) {
              console.log(`  -> Searching for selector: "${selector.substring(0, 80)}..."`);
              const elementNode = $$(selector).first();

              if (elementNode.length > 0) {
                console.log(`    -> SUCCESS: Found element!`);
                let contextHtml = elementNode.parent().html();

                if (contextHtml && contextHtml.length > 2000) {
                  contextHtml = $$.html(elementNode);
                }

                analysisItems.push({
                  issue_type: categoryName,
                  description: item.description,
                  selector: selector,
                  context_html: contextHtml ? contextHtml.trim() : ""
                });
              } else {
                console.log(`    -> WARNING: Could not find element for selector.`);
              }
            }
          }
        }
      }
    }
  }
}

// --- 5. FINAL OUTPUT ---
console.log(`\n--- PROCESSING COMPLETE ---`);
console.log(`Total items collected for AI: ${analysisItems.length}`);

return {
  json: {
    analysisItems
  }
};
