// Get raw klines array
const klines = $input.first().json.klines;

// Parameters
const period = 20;
const stdMultiplier = 2;

// Validate type
if (!Array.isArray(klines)) {
  throw new Error("klines is not an array");
}

// Extract closing prices
const closes = klines.map(k => parseFloat(k[4]));

// Ensure enough data
if (closes.length < period) {
  throw new Error(`Not enough data: got ${closes.length}, expected at least ${period}`);
}

// BB calculation logic
function calculateBB(prices, period) {
  const result = [];
  for (let i = period - 1; i < prices.length; i++) {
    const slice = prices.slice(i - period + 1, i + 1);
    const mean = slice.reduce((sum, val) => sum + val, 0) / period;
    const variance = slice.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / period;
    const stdDev = Math.sqrt(variance);

    result.push({
      close: prices[i],
      basis: mean,
      upper: mean + stdMultiplier * stdDev,
      lower: mean - stdMultiplier * stdDev,
      timestamp: klines[i][0]
    });
  }
  return result;
}

// Calculate bands
const bands = calculateBB(closes, period);

// Format output
return bands.map(b => ({
  json: {
    timestamp: b.timestamp,
    close: b.close,
    bb_basis: b.basis,
    bb_upper: b.upper,
    bb_lower: b.lower
  }
}));

