const klines = $input.first().json.klines;

// Parameters
const period = 14;

// Parse high, low, close arrays
const highs = klines.map(k => parseFloat(k[2]));
const lows = klines.map(k => parseFloat(k[3]));
const closes = klines.map(k => parseFloat(k[4]));

// Validation
if (closes.length < period + 1) {
  throw new Error(`Not enough data for ADX. Need at least ${period + 1}, got ${closes.length}`);
}

// Initialize arrays
const tr = [];
const plusDM = [];
const minusDM = [];

// Step 1: Calculate TR, +DM, -DM
for (let i = 1; i < klines.length; i++) {
  const highDiff = highs[i] - highs[i - 1];
  const lowDiff = lows[i - 1] - lows[i];
  const upMove = highDiff > 0 && highDiff > lowDiff ? highDiff : 0;
  const downMove = lowDiff > 0 && lowDiff > highDiff ? lowDiff : 0;

  const trueRange = Math.max(
    highs[i] - lows[i],
    Math.abs(highs[i] - closes[i - 1]),
    Math.abs(lows[i] - closes[i - 1])
  );

  tr.push(trueRange);
  plusDM.push(upMove);
  minusDM.push(downMove);
}

// Step 2: Smooth TR, +DM, -DM and calculate DI
const smoothedTR = [tr.slice(0, period).reduce((a, b) => a + b, 0)];
const smoothedPlusDM = [plusDM.slice(0, period).reduce((a, b) => a + b, 0)];
const smoothedMinusDM = [minusDM.slice(0, period).reduce((a, b) => a + b, 0)];

for (let i = period; i < tr.length; i++) {
  smoothedTR.push(smoothedTR[smoothedTR.length - 1] - smoothedTR[smoothedTR.length - 1] / period + tr[i]);
  smoothedPlusDM.push(smoothedPlusDM[smoothedPlusDM.length - 1] - smoothedPlusDM[smoothedPlusDM.length - 1] / period + plusDM[i]);
  smoothedMinusDM.push(smoothedMinusDM[smoothedMinusDM.length - 1] - smoothedMinusDM[smoothedMinusDM.length - 1] / period + minusDM[i]);
}

const plusDI = smoothedPlusDM.map((val, i) => 100 * val / smoothedTR[i]);
const minusDI = smoothedMinusDM.map((val, i) => 100 * val / smoothedTR[i]);

// Step 3: Calculate DX
const dx = plusDI.map((val, i) => {
  const diff = Math.abs(plusDI[i] - minusDI[i]);
  const sum = plusDI[i] + minusDI[i];
  return sum === 0 ? 0 : 100 * (diff / sum);
});

// Step 4: Smooth DX into ADX
const adx = [];
const firstADX = dx.slice(0, period).reduce((a, b) => a + b, 0) / period;
adx.push(firstADX);

for (let i = period; i < dx.length; i++) {
  const newADX = (adx[adx.length - 1] * (period - 1) + dx[i]) / period;
  adx.push(newADX);
}

// Step 5: Final result formatting
const output = [];

for (let i = 0; i < adx.length; i++) {
  const index = i + (2 * period); // account for offset
  if (klines[index]) {
    output.push({
      timestamp: klines[index][0],
      close: closes[index],
      adx: adx[i],
      plusDI: plusDI[i + period],
      minusDI: minusDI[i + period]
    });
  }
}

return output.map(r => ({ json: r }));

