  return {
    chapter_no: index + 1,
    chapter_name: item.json.chapter_name || `Chapter ${index + 1}`,
    chapter_text: item.json.chapter_text
  };
});

// Create the final JSON structure
const finalJson = {
  video_title: $('Fetch Video Details').first().json.snippet.title,  // Replace with actual video title
  video_description: $('Fetch Video Details').first().json.snippet.description,  // Replace with actual video description
  merged_text: chapters
};

// Return the final JSON structure
return [
  {
    json: finalJson
  }
];

// Retrieve items from the previous node
const items = $input.all();
let finalUrl = '';

for (const item of items) {
  const subtitles = item.json.subtitles;

  // Prioritize "English (auto-generated)"
  let subtitle = subtitles.find(
    sub => sub.languageName === 'English (auto-generated)' && sub.languageCode === 'en'
  );

  // If "English (auto-generated)" is not found, fall back to "English"
  if (!subtitle) {
    subtitle = subtitles.find(
      sub => sub.languageName === 'English' && sub.languageCode === 'en'
    );
  }

  if (subtitle) {
    // Ensure the URL contains 'fmt=srv1'
    let url = subtitle.url;

    if (!url.includes('fmt=srv1')) {
      // If 'fmt=srv1' is not in the URL, add or replace the fmt parameter
      if (url.includes('fmt=')) {
        url = url.replace(/fmt=[^&]*/, 'fmt=srv1');
      } else {
        url += '&fmt=srv1';
      }
    }
    // Set the final URL
    finalUrl = url;
    break; // Stop after finding the first match
  }
}

// Return only the final URL
return [{ json: { url: finalUrl } }];
