// Initialize a single array to hold all data items
const mergedData = [];

// Loop over all input items
for (const item of $input.all()) {
  if (item.json.data && Array.isArray(item.json.data)) {
    mergedData.push(...item.json.data); // Spread and merge the data arrays
  }
}

// Return a single item with the combined data array
return [
  {
    json: {
      data: mergedData
    }
  }
];

const allowedFilters = [
  "country_code",
  "region_country_code",
  "company_country_code",
  "company_region_country_code",
  "company_size",
  "company_revenue",
  "company_age",
  "google_category",
  "naics_category",
  "linkedin_category",
  "company_name",
  "number_of_locations",
  "city_region_country",
  "website_keywords",
  "has_email",
  "has_phone_number",
  "job_level",
  "job_department",
  "job_title",
  "business_id",
  "total_experience_months",
  "current_role_months"
];

const validCompanySizes = ['1-10', '11-50', '51-200', '201-500', '501-1000', '1001-5000', '5001-10000', '10001+'];
const validAges = ['0-3', '3-6', '6-10', '10-20', '20+'];
const validLocations = ['0-1', '2-5', '6-20', '21-50', '51-100', '101-1000', '1001+'];
const validRevenue = [
  "0-500K", "500K-1M", "1M-5M", "5M-10M", "10M-25M", "25M-75M", "75M-200M", "200M-500M",
  "500M-1B", "1B-10B", "10B-100B", "100B-1T", "1T-10T", "10T+"
];
const validJobLevel = ["director", "manager", "vp", "partner", "cxo", "non-managerial", "senior", "entry", "training", "unpaid"];
const validDepartment = [
  "customer service", "design", "education", "engineering", "finance", "general", "health", "human resources",
  "legal", "marketing", "media", "operations", "public relations", "real estate", "sales", "trades", "unknown"
];

const countryCodeRegex = /^(aw|af|ao|ai|ax|al|ad|ae|ar|am|as|aq|tf|ag|au|at|az|bi|be|bj|bq|bf|bd|bg|bh|bs|ba|bl|by|bz|bm|bo|br|bb|bn|bt|bv|bw|cf|ca|cc|ch|cl|cn|ci|cm|cd|cg|ck|co|km|cv|cr|cu|cw|cx|ky|cy|cz|de|dj|dm|dk|do|dz|ec|eg|er|eh|es|ee|et|fi|fj|fk|fr|fo|fm|ga|gb|ge|gg|gh|gi|gn|gp|gm|gw|gq|gr|gd|gl|gt|gf|gu|gy|hk|hm|hn|hr|ht|hu|id|im|in|io|ie|ir|iq|is|il|it|jm|je|jo|jp|kz|ke|kg|kh|ki|kn|kr|kw|la|lb|lr|ly|lc|li|lk|ls|lt|lu|lv|mo|mf|ma|mc|md|mg|mv|mx|mh|mk|ml|mt|mm|me|mn|mp|mz|mr|ms|mq|mu|mw|my|yt|na|nc|ne|nf|ng|ni|nu|nl|no|np|nr|nz|om|pk|pa|pn|pe|ph|pw|pg|pl|pr|kp|pt|py|ps|pf|qa|re|ro|ru|rw|sa|sd|sn|sg|gs|sh|sj|sb|sl|sv|sm|so|pm|rs|ss|st|sr|sk|si|se|sz|sx|sc|sy|tc|td|tg|th|tj|tk|tm|tl|to|tt|tn|tr|tv|tw|tz|ug|ua|um|uy|us|uz|va|vc|ve|vg|vi|vn|vu|wf|ws|xk|ye|za|zm|zw)$/;

const item = $input.first().json;
const output = item.output || {};
const filters = output.filters || {};
const errors = [];

for (const key of Object.keys(filters)) {
  const filter = filters[key];

  if (!allowedFilters.includes(key)) {
    errors.push(`Invalid filter key: '${key}'`);
    continue;
  }

  if ('value' in filter) {
    if (typeof filter.value !== 'boolean') {
      errors.push(`Filter '${key}' has a 'value' but it's not a boolean.`);
    }
    continue;
  }

  if ('values' in filter) {
    if (!Array.isArray(filter.values) || filter.values.length === 0) {
      errors.push(`Filter '${key}' must contain a non-empty 'values' array.`);
      continue;
    }

    // Normalize to lowercase for fields that require it
    if (["job_level", "job_department", "country_code", "region_country_code", "company_country_code", "company_region_country_code"].includes(key)) {
      filter.values = filter.values.map(v => typeof v === 'string' ? v.toLowerCase() : v);
    }

    const validateValues = (validList, keyName) => {
      const invalid = filter.values.filter(v => !validList.includes(v));
      if (invalid.length > 0) {
        errors.push(`Invalid ${keyName} values: ${invalid.join(', ')}. Valid values are: ${validList.join(', ')}`);
      }
    };

    switch (key) {
      case 'company_size':
        validateValues(validCompanySizes, 'company_size');
        break;
      case 'company_age':
        validateValues(validAges, 'company_age');
        break;
      case 'number_of_locations':
        validateValues(validLocations, 'number_of_locations');
        break;
      case 'company_revenue':
        validateValues(validRevenue, 'company_revenue');
        break;
      case 'job_level':
        validateValues(validJobLevel, 'job_level');
        break;
      case 'job_department':
        validateValues(validDepartment, 'job_department');
        break;
      case 'country_code':
        const invalid = filter.values.filter(v => !countryCodeRegex.test(v));
        if (invalid.length > 0) {
          errors.push(`Invalid country_code values: ${invalid.join(', ')}. Must be ISO 3166-1 alpha-2 codes.`);
        }
        break;
    }

    const unique = new Set(filter.values);
    if (unique.size < filter.values.length) {
      errors.push(`Duplicate values found in filter '${key}'`);
    }

    continue;
  }

  // Handle total_experience_months and current_role_months as a range objects
  if (key === 'total_experience_months' || key === 'current_role_months') {
  const range = filter;

  if (!('gte' in range) && !('lte' in range)) {
    errors.push(`Filter '${key}' must include at least 'gte' or 'lte'.`);
    continue;
  }

  if ('gte' in range && typeof range.gte !== 'number') {
    errors.push(`Filter '${key}' has a non-numeric 'gte' value.`);
  }

  if ('lte' in range && typeof range.lte !== 'number') {
    errors.push(`Filter '${key}' has a non-numeric 'lte' value.`);
  }

  if ('gte' in range && 'lte' in range && range.gte > range.lte) {
    errors.push(`Filter '${key}' has 'gte' greater than 'lte'.`);
  }

  continue;
}

  errors.push(`Filter '${key}' must include either 'value', 'values', or a valid range.`);
}

return [
  {
    json: {
      ...item,
      isValid: errors.length === 0,
      validationErrors: errors
    }
  }
];

const sessionId = $('When chat message received').first().json.sessionId;
const userQuery = $('When chat message received').first().json.chatInput;
const aiOutput = $('AI Agent').first().json.output;
const validationErrors = $('API Call Validation').first().json.validationErrors;

// Safely stringify output and errors
const formattedOutput = typeof aiOutput === 'object' ? JSON.stringify(aiOutput, null, 2) : aiOutput;
const formattedErrors = Array.isArray(validationErrors) ? validationErrors.join('\n- ') : validationErrors;

return [
  {
    sessionId,
    action: "sendMessage",
    source: "validation",
    errorInput: 
`Your response did not pass validation.

üìù **User Query**:
${userQuery}

ü§ñ **Your Output**:
${formattedOutput}

‚ö†Ô∏è **Validation Errors**:
- ${formattedErrors}

üîß Please review the validation errors and regenerate a corrected output. 
Ensure that all values strictly match the allowed formats and valid categories.

Reformulate your response accordingly.`
  }
];

const chat = $json.chatInput;
const error = $json.errorInput;

return [
  {
    json: {
      ...$json,
      combinedInput: error ?? chat
    }
  }
];

