parameters.jsCode:
const candidates = $input.first().json.data;

if (!Array.isArray(candidates) || candidates.length === 0) {
  throw new Error("No candidates found in data.");
}

const result = candidates.map(candidate => {
  return {
    json: {
      prospect_id: candidate.prospect_id,  
      full_name: candidate.full_name,
      first_name: candidate.first_name,
      last_name: candidate.last_name,
      title: candidate.job_title,
      department: candidate.job_department,
      seniority: candidate.job_seniority_level?.join(', ') || '',
      company: candidate.company_name,
      location: `${candidate.city}, ${candidate.region_name}, ${candidate.country_name}`,
      linkedin: candidate.linkedin,
      email: candidate.professional_email_hashed || '',
      experience: candidate.experience?.join(' | ') || '',
      skills: candidate.skills?.join(', ') || '',
      interests: candidate.interests?.join(', ') || '',
      business_id: candidate.business_id
    }
  };
});

return result;

const prospectIds = $input.all()
  .map(item => String(item.json.prospect_id).trim()) // Force string and trim whitespace
  .filter(Boolean); // remove empty/null

return [{
  json: {
    prospect_ids: prospectIds
  }
}];

// Get full profile data from the HTTP Request node
const profiles = $('Explorium Prospects API Call').first().json.data;

// Get contact enrichment data from the current input
const contactEnrichments = $input.first().json.data;

// Step 1: Create a lookup table for enriched contact info
const contactMap = {};
for (const contact of contactEnrichments) {
  contactMap[contact.prospect_id] = contact.data;
}

// Step 2: Merge profile with email info
const result = profiles.map(profile => {
  const contact = contactMap[profile.prospect_id];

  // Determine the best email
  let email = profile.email || "";
  if (contact) {
    email = contact.professions_email ||
            (contact.emails?.find(e => e.type === "professional")?.address) ||
            (contact.emails?.[0]?.address) ||
            "";
  }

  return {
    json: {
      ...profile,
      email // now includes enriched email if found
    }
  };
});

return result;
