// n8n Code Node - Email Formatter for AI Newsletter
// This code formats the AI agent output for email sending

// Get the AI agent output from the previous node
const aiOutput = $input.all()[0].json.output || $input.all()[0].json.text || $input.all()[0].json;

// Extract the newsletter content (remove the prefix text)
let newsletterContent = '';
if (typeof aiOutput === 'object' && aiOutput.content) {
  newsletterContent = aiOutput.content;
} else if (typeof aiOutput === 'string') {
  // Remove the "this is the output from the ai agent:" prefix if it exists
  newsletterContent = aiOutput.replace(/^this is the output from the ai agent:\s*/i, '');
} else {
  newsletterContent = JSON.stringify(aiOutput);
}

// Clean up the newsletter content - remove \n literals and format properly
newsletterContent = newsletterContent
  .replace(/\\n/g, '\n')  // Convert literal \n to actual newlines
  .replace(/^\s+|\s+$/g, '')  // Trim whitespace
  .replace(/\n{3,}/g, '\n\n');  // Replace multiple newlines with double newlines

// Generate email subject with current date
