// Generate HTML version of the email for better formatting
function generateHTMLEmailBody() {
  // Convert markdown-like content to basic HTML
  let htmlContent = newsletterContent
    .replace(/^# (.*$)/gm, '<h1 style="color: #333; border-bottom: 2px solid #667eea; padding-bottom: 10px;">$1</h1>')
    .replace(/^## (.*$)/gm, '<h2 style="color: #444; margin-top: 30px; margin-bottom: 15px;">$1</h2>')
    .replace(/^### (.*$)/gm, '<h3 style="color: #555; margin-top: 20px; margin-bottom: 10px;">$1</h3>')
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    .replace(/\[([^\]]+)\]\(([^)]+)\)/g, '<a href="$2" style="color: #667eea; text-decoration: none;">$1</a>')
    .replace(/^- (.*$)/gm, '<li style="margin-bottom: 8px;">$1</li>')
    .replace(/^> (.*$)/gm, '<blockquote style="border-left: 4px solid #667eea; padding-left: 20px; margin: 20px 0; font-style: italic; color: #666;">$1</blockquote>')
    .replace(/---/g, '<hr style="border: none; border-top: 2px solid #eee; margin: 30px 0;">')
    .replace(/\n\n/g, '</p><p style="line-height: 1.6; margin-bottom: 15px;">')
    .replace(/\n/g, '<br>');

  // Wrap list items in ul tags
  htmlContent = htmlContent.replace(/(<li[^>]*>.*?<\/li>)/gs, '<ul style="padding-left: 20px; margin-bottom: 20px;">$1</ul>');

  const htmlBody = `
<!DOCTYPE html>
