// Get the response from the LLM
let rawResponse = $json.text;

// Always use regex extraction to avoid JSON parsing issues
console.log("Using regex extraction method");

// Extract content using regex - handles both complete and truncated responses
const postMatch = rawResponse.match(/"post_content":\s*"((?:[^"\\]|\\[\s\S])*)"/);
const imageMatch = rawResponse.match(/"image_description":\s*"((?:[^"\\]|\\[\s\S])*)"/);

if (postMatch) {
 // Clean up the extracted content
 let postContent = postMatch[1]
   .replace(/\\n/g, '\n')
   .replace(/\\"/g, '"')
   .replace(/\\t/g, '\t');

 let imageDescription = imageMatch ? 
   imageMatch[1].replace(/\\n/g, '\n').replace(/\\"/g, '"') : 
   "Professional LinkedIn post visual";

 return {
   post_content: postContent,
   image_description: imageDescription,
   parsing_method: "regex_extraction",
   was_truncated: !postMatch[1].endsWith('"')
 };
}

return {
 error: "Could not extract post content",
 raw_response: rawResponse.substring(0, 500) + "...", // Show first 500 chars for debugging
 debug_info: "No post_content found in response"
};
