const items = [];

console.log(`Code Node (Prepare Upstream URL): Received ${$input.all().length} input items.`);

for (const item of $input.all()) {
  const forkRepo = item.json;

  console.log(`Processing repo: ${forkRepo.full_name || 'N/A'}`);

  if (forkRepo && forkRepo.fork && forkRepo.parent) {
    const upstreamRepo = forkRepo.parent;

    const upstreamOwner = upstreamRepo.owner.login;
    const upstreamName = upstreamRepo.name;
    const upstreamDefaultBranch = upstreamRepo.default_branch;

    const forkOwner = forkRepo.owner.login;
    const forkName = forkRepo.name;
    const forkDefaultBranch = forkRepo.default_branch;

    const compareUrl = `https://api.github.com/repos/${upstreamOwner}/${upstreamName}/compare/${upstreamDefaultBranch}...${forkOwner}:${forkDefaultBranch}`;

    const newItem = {
      json: {
        // This is the URL the HTTP Request node will use
        compareApiUrl: compareUrl,

        // <<<< CRITICAL: Embed ALL original data here in a nested object >>>>
        // This relies on the HTTP Request node passing this nested object through
        original_repo_data: {
          repoName: forkRepo.full_name,
          upstreamUrl: upstreamRepo.html_url,
          forkDefaultBranch: forkDefaultBranch,
          upstreamDefaultBranch: upstreamDefaultBranch,
          upstreamRepoName: upstreamName,
          upstreamOwner: upstreamOwner,
        }
        // <<<< END CRITICAL >>>>
      }
    };

    items.push(newItem);
    console.log(`--> Prepared item for: ${forkRepo.full_name}. Compare URL: ${compareUrl}`);

  } else {
    let skipReason = "Not a fork or missing parent";
    if (forkRepo) {
        if (!forkRepo.fork) skipReason = "Not marked as a fork";
        if (!forkRepo.parent) skipReason = "Missing parent (upstream) repository";
    }
    console.log(`Skipping repo: ${forkRepo.full_name || 'N/A'} - Reason: ${skipReason}`);
  }
}

console.log(`Code Node (Prepare Upstream URL): Returning ${items.length} items.`);

return items;

const items = [];

console.log(`Code Node (Process Combined Result): Received ${$input.all().length} input items.`);

for (const item of $input.all()) {
  // Original repo data is picked up from 2 nodes earlier
  const originalRepoData = $('Prepare Upstream URL').first().json.original_repo_data

  // API response data is now in item.json
  const apiResponse = item.json;
  console.log("apiResponse",apiResponse);


  // Debugging: Check if data is available
  if (typeof apiResponse.behind_by === 'undefined' || typeof apiResponse.ahead_by === 'undefined') {
      console.error(`ERROR: Missing or malformed data after merge! Original:`, originalRepoData, `API:`, apiResponse);
      // Push an error item or skip
      items.push({
          json: {
              repoName: originalRepoData?.repoName || 'N/A', // Use optional chaining for safety
              status: "ERROR_PROCESSING_MERGE",
              commitsBehind: -1,
              commitsAhead: -1
          }
      });
      continue;
  }

  const commitsBehind = apiResponse.behind_by;
  const commitsAhead = apiResponse.ahead_by;
  const status = commitsBehind > 0 ? "BEHIND" : (commitsAhead > 0 ? "AHEAD" : "UP-TO-DATE");



  const processedItem = {
    json: {
      repoName: originalRepoData.repoName,
      upstreamUrl: originalRepoData.upstreamUrl,
      forkDefaultBranch: originalRepoData.forkDefaultBranch,
      upstreamDefaultBranch: originalRepoData.upstreamDefaultBranch,
      upstreamRepoName: originalRepoData.upstreamRepoName,
      upstreamOwner: originalRepoData.upstreamOwner,
      status: status,
      commitsBehind: commitsBehind,
      commitsAhead: commitsAhead
    }
  };
  items.push(processedItem);
  console.log(`--> Processed ${processedItem.json.repoName}: Status=${processedItem.json.status}, Behind=${processedItem.json.commitsBehind}, Ahead=${processedItem.json.commitsAhead}`);
}
console.log(`Code Node (Process Combined Result): Returning ${items.length} items.`);
return items;

const items = $input.all();
let telegramMessage = `*GitHub Fork Status Report - ${new Date().toLocaleDateString()}*\n\n`;

// Initialize counters for summary
let reposBehind = 0;
let reposAhead = 0;
let reposUpToDate = 0;

// ... (previous code)

// Build the table header
telegramMessage += `| Ahead | Behind | Repository |\n`;
telegramMessage += `|------------|------------|-----------------------|\n`;

// Populate table rows
for (const item of items) {
    const data = item.json;
    const repoName = data.repoName;
    const commitsAhead = data.commitsAhead;
    const commitsBehind = data.commitsBehind;

    // Update counters for summary
    if (commitsBehind > 0) {
        reposBehind++;
    } else if (commitsAhead > 0) {
        reposAhead++;
    } else {
        reposUpToDate++;
        continue;
    }

    // --- CRITICAL FIX START ---
    // Ensure you use BACKTICKS ( ` ) for the string literal
    // And standard JavaScript template literal syntax ${variableName}

    // Escape characters in the repo name for Markdown if necessary, but usually not for links like this
    // For general text: `repoName.replace(/[-_.*[\]()~`>#+\-=|{}.!]/g, '\\$&')`
    // But for links, usually the raw name is fine within `[]`
    const escapedRepoName = repoName.replace(/_/g, '\\_').replace(/\*/g, '\\*').replace(/\[/g, '\\[').replace(/\]/g, '\\]').replace(/\(/g, '\\(').replace(/\)/g, '\\)').replace(/~/g, '\\~').replace(/`/g, '\\`').replace(/>/g, '\\>').replace(/#/g, '\\#').replace(/\+/g, '\\+').replace(/-/g, '\\-').replace(/=/g, '\\=').replace(/\|/g, '\\|').replace(/{/g, '\\{').replace(/}/g, '\\}').replace(/\./g, '\\.').replace(/!/g, '\\!');


    // Use [text](url) format for Telegram MarkdownV2 links
    const repoLink = `[${escapedRepoName}](https://github.com/${repoName})`; // Changed from <url|text>
    // --- CRITICAL FIX END ---
  // Format commitsAhead and commitsBehind to be 4 characters wide, right-aligned
    const formattedAhead = String(commitsAhead).padStart(5, '0');
    const formattedBehind = String(commitsBehind).padStart(5, '0');
    telegramMessage += `| ${formattedAhead} | ${formattedBehind} | ${repoLink} |\n`;
}

// ... (rest of the code)


return [{ json: { telegramMessage: telegramMessage } }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.repos = processCommand(item.json.message.text);
}

return $input.all();

function processCommand(input) {
  const parts = input.trim().split(" "); // Split by space and remove extra spaces
  const command = parts[0];
  const number = parseInt(parts[1], 10); // Convert second part to an integer

  let repos = 0;

  if (command === "/forkcheck") {
    if (Number.isInteger(number) && number > 0) {
      repos = number;
    } else {
      repos = 1000; // Default value if blank or 0
    }
    console.log(`Repos set to: ${repos}`);
  } else {
    console.log("Invalid command.");
  }
return repos;
}
