// Group orders by customer and calculate RFM
const orders = $input.all();
const customers = {};
const today = new Date();

// Process orders
orders.forEach(order => {
  const customerId = order.json.customer_id || order.json.billing.email;
  if (!customerId) return;

  if (!customers[customerId]) {
    customers[customerId] = {
      customerId: customerId,
      customerName: `${order.json.billing.first_name} ${order.json.billing.last_name}`,
      email: order.json.billing.email,
      orders: [],
      totalSpent: 0,
      orderCount: 0
    };
  }

  const orderDate = new Date(order.json.date_created);
  customers[customerId].orders.push({
    date: orderDate,
    total: parseFloat(order.json.total)
  });
  customers[customerId].totalSpent += parseFloat(order.json.total);
  customers[customerId].orderCount++;
});

// Calculate RFM values
const customerData = Object.values(customers).map(customer => {
  // Recency: Number of days since last purchase
  const lastOrderDate = Math.max(...customer.orders.map(o => o.date));
  const recency = Math.floor((today - new Date(lastOrderDate)) / (1000 * 60 * 60 * 24));

  // Frequency: Number of orders
  const frequency = customer.orderCount;

  // Monetary: Total spent
  const monetary = customer.totalSpent;

  return {
    customerId: customer.customerId,
    customerName: customer.customerName,
    email: customer.email,
    recency: recency,
    frequency: frequency,
    monetary: monetary,
    lastOrderDate: new Date(lastOrderDate).toISOString().split('T')[0]
  };
});

// Sort for quartile calculations
const sortedByRecency = [...customerData].sort((a, b) => a.recency - b.recency);
const sortedByFrequency = [...customerData].sort((a, b) => b.frequency - a.frequency);
const sortedByMonetary = [...customerData].sort((a, b) => b.monetary - a.monetary);

// Quartile calculation helper
function getQuartile(sortedArray, value, field, ascending = false) {
  const index = sortedArray.findIndex(item => item[field] === value);
  const quartile = Math.ceil((index + 1) / sortedArray.length * 4);
  return ascending ? quartile : 5 - quartile;
}

// Assign RFM scores
const rfmScores = customerData.map(customer => {
  const rScore = getQuartile(sortedByRecency, customer.recency, 'recency', true);
  const fScore = getQuartile(sortedByFrequency, customer.frequency, 'frequency');
  const mScore = getQuartile(sortedByMonetary, customer.monetary, 'monetary');

  const rfmScore = `${rScore}${fScore}${mScore}`;

  // Determine segment
  let segment = '';
  if (rScore >= 4 && fScore >= 4 && mScore >= 4) {
    segment = 'Champions';
  } else if (rScore >= 3 && fScore >= 3 && mScore >= 4) {
    segment = 'Loyal Customers';
  } else if (rScore >= 3 && fScore >= 1 && mScore >= 3) {
    segment = 'Potential Loyalists';
  } else if (rScore >= 4 && fScore <= 2) {
    segment = 'New Customers';
  } else if (rScore >= 3 && fScore >= 3 && mScore <= 2) {
    segment = 'Promising';
  } else if (rScore <= 2 && fScore >= 3) {
    segment = 'Need Attention';
  } else if (rScore <= 2 && fScore <= 2 && mScore >= 3) {
    segment = 'About to Sleep';
  } else if (rScore <= 2 && fScore >= 3 && mScore >= 3) {
    segment = 'At Risk';
  } else if (rScore <= 2 && fScore <= 2 && mScore <= 2) {
    segment = 'Lost';
  } else {
    segment = 'Others';
  }

  return {
    ...customer,
    rScore,
    fScore,
    mScore,
    rfmScore,
    segment,
    avgOrderValue: customer.monetary / customer.frequency
  };
});

return rfmScores;

const items = $input.all();

// Segment translation and priority definitions
const segmentDetails = {
  'Champions': { fa: 'Champions', priority: 1, action: 'Send VIP offer and exclusive discount code' },
  'Loyal Customers': { fa: 'Loyal Customers', priority: 2, action: 'Invite to loyalty club and introduce new products' },
  'Potential Loyalists': { fa: 'Potential Loyalists', priority: 3, action: 'Encourage more interaction and suggest complementary products' },
  'New Customers': { fa: 'New Customers', priority: 4, action: 'Send welcome message and encourage second purchase' },
  'Promising': { fa: 'Promising', priority: 5, action: 'Send product reminders and additional education' },
  'Need Attention': { fa: 'Need Attention', priority: 6, action: 'Investigate reasons for inactivity and send comeback campaign' },
  'About to Sleep': { fa: 'About to Sleep', priority: 7, action: 'Send new products and return incentives' },
  'At Risk': { fa: 'At Risk', priority: 8, action: 'Send personalized message with heavy discount' },
  'Lost': { fa: 'Lost', priority: 9, action: 'Offer return deal and "We Miss You" campaign' },
  'Others': { fa: 'Others', priority: 10, action: 'Manual review for behavior analysis' },
};

// Count customers per segment
const segmentCounts = {};

for (const item of items) {
  const seg = item.json.segment || 'Others';
  if (!segmentCounts[seg]) segmentCounts[seg] = 0;
  segmentCounts[seg]++;
}

// Build HTML table
let html = `
<h2 style="text-align: center;">RFM Summary Report - ${new Date().toISOString().split('T')[0]}</h2>
<table border="1" cellpadding="8" cellspacing="0" style="width:90%; margin:auto; border-collapse:collapse; font-family:sans-serif;">
  <thead style="background:#e6f2ff;">
    <tr>
      <th>Segment</th>
      <th>Customer Count</th>
      <th>Marketing Suggestion</th>
    </tr>
  </thead>
  <tbody>
`;

Object.entries(segmentCounts)
  .map(([key, count]) => ({
    key,
    count,
    priority: segmentDetails[key]?.priority || 999,
    label: segmentDetails[key]?.fa || key,
    action: segmentDetails[key]?.action || '-'
  }))
  .sort((a, b) => a.priority - b.priority)
  .forEach(({ label, count, action }) => {
    html += `
      <tr>
        <td>${label}</td>
        <td>${count}</td>
        <td>${action}</td>
      </tr>
    `;
  });

html += `
  </tbody>
</table>
`;

return [{ json: { html } }];
