const script = $input.first().json.text;
const voiceName = $input.first().json.voiceName || "Kore";

return {
  json: {
    requestBody: {
      contents: [{
        parts: [{
          text: script
        }]
      }],
      generationConfig: {
        responseModalities: ["AUDIO"],
        speechConfig: {
          voiceConfig: {
            prebuiltVoiceConfig: {
              voiceName: voiceName
            }
          }
        }
      }
    }
  }
};

const response = $input.all()[0].json;

console.log('=== GEMINI TTS DEBUG ===');
console.log('Response status:', response.candidates?.[0]?.finishReason);

if (response.candidates?.[0]?.finishReason === 'STOP') {
  try {
    // Get the base64 audio data
    const base64AudioData = response.candidates[0].content.parts[0].inlineData.data;
    const mimeType = response.candidates[0].content.parts[0].inlineData.mimeType;

    console.log('Audio data length:', base64AudioData.length);
    console.log('Mime type:', mimeType);

    // CRITICAL FIX: Properly decode base64 to actual audio bytes
    const actualAudioBytes = Buffer.from(base64AudioData, 'base64');

    console.log('Decoded audio size:', actualAudioBytes.length);

    // Create WAV file with proper PCM16 format
    const sampleRate = 24000;
    const channels = 1;
    const bitsPerSample = 16;

    // Create WAV header
    const wavHeader = createWavHeader(actualAudioBytes.length, sampleRate, channels, bitsPerSample);
    const wavFile = Buffer.concat([wavHeader, actualAudioBytes]);

    const filename = `tts_fixed_${Date.now()}.wav`;

    return {
      json: {
        success: true,
        filename: filename,
        audioSize: wavFile.length,
        audioSizeMB: (wavFile.length / 1024 / 1024).toFixed(2),
        originalBase64Length: base64AudioData.length,
        decodedAudioLength: actualAudioBytes.length
      },
      binary: {
        audio: {
          data: wavFile,
          mimeType: 'audio/wav',
          fileName: filename
        }
      }
    };

  } catch (error) {
    return {
      json: {
        success: false,
        error: error.message,
        step: 'audio_processing'
      }
    };
  }
} else {
  return {
    json: {
      success: false,
      error: 'TTS generation failed',
      finishReason: response.candidates?.[0]?.finishReason || 'unknown',
      response: response
    }
  };
}

// Helper function to create WAV header
function createWavHeader(dataLength, sampleRate, channels, bitsPerSample) {
  const byteRate = sampleRate * channels * bitsPerSample / 8;
  const blockAlign = channels * bitsPerSample / 8;
  const header = Buffer.alloc(44);

  // "RIFF" chunk descriptor
  header.write('RIFF', 0);
  header.writeUInt32LE(36 + dataLength, 4);
  header.write('WAVE', 8);

  // "fmt " sub-chunk
  header.write('fmt ', 12);
  header.writeUInt32LE(16, 16); // Sub-chunk size
  header.writeUInt16LE(1, 20);  // Audio format (PCM)
  header.writeUInt16LE(channels, 22);
  header.writeUInt32LE(sampleRate, 24);
  header.writeUInt32LE(byteRate, 28);
  header.writeUInt16LE(blockAlign, 32);
  header.writeUInt16LE(bitsPerSample, 34);

  // "data" sub-chunk
  header.write('data', 36);
  header.writeUInt32LE(dataLength, 40);

  return header;
}
