parameters.functionCode:
// Analizza il piano di orchestrazione e imposta per il looping
let rawPlan = $json.choices[0].message.content;
let plan;

try {
  plan = JSON.parse(rawPlan);
} catch (e) {
  // Se il parsing fallisce, assumi che sia una stringa di array grezza
  plan = rawPlan;
}

// Estrai in modo sicuro la sequenza degli agenti:
// Se 'plan' è un oggetto e ha una chiave 'agents', usala.
// Altrimenti, se 'plan' è un array, usalo direttamente.
// Altrimenti, predefinisci un array vuoto.
const agentSequence = Array.isArray(plan) ? plan : (plan && plan.agents && Array.isArray(plan.agents) ? plan.agents : []);

// Memorizza la sequenza e l'indice corrente per il loop
$workflow.agentSequence = agentSequence;
$workflow.currentAgentIndex = 0;
$workflow.redraftLoopCount = 0; // Inizializza il contatore dei cicli di rielaborazione

// Passa i dati di input al primo agente, assicurandosi che $items[0].json.body esista
const initialInput = $items[0] && $items[0].json && $items[0].json.body ? $items[0].json.body : {};

return [{ json: { ...initialInput, agentToRun: agentSequence[0] || null } }];

// Ottieni il nome dell'agente corrente
const agentName = $workflow.agentSequence[$workflow.currentAgentIndex];

// Prepara i dati per l'esecuzione dell'agente
// Passa l'output del passo precedente (o l'input iniziale).
// Se stiamo rielaborando, l'input dell'agente dovrebbe includere il feedback di rielaborazione.
const previousOutput = $json.output || $json.body.input; // Assumendo che l'output dell'agente sia memorizzato nella chiave 'output'
const agentInput = $json.redraftInput || previousOutput; // Usa redraftInput se presente, altrimenti previousOutput

return [{ json: { ...$json, agentToRun: agentName, agentInput: agentInput } }];

// Prepara i metadati per il contenuto dell'Handbook.
// Assicurati che l'input originale contenga 'title' e 'tags' o imposta dei valori predefiniti.
const title = $json.body.title || 'Untitled Handbook Entry';
const tags = $json.body.tags || [];
const phase = $json.body.phase || 'draft'; // Fase iniziale, può essere 'final' dopo l'approvazione
const rhythm = $json.body.rhythm || 'on-demand'; // Ritmo cognitivo

return [{ json: { ...$json, handbookTitle: title, handbookTags: tags, handbookPhase: phase, handbookRhythm: rhythm } }];

// Prepara il contenuto proposto dall'Archivista per la revisione umana, inclusa la formattazione YAML.
// Assicurati che l'input dell'agente (il contenuto generato) sia disponibile.
const proposedContent = $json.agentInput;
const title = $json.handbookTitle;
const tags = $json.handbookTags;
const phase = $json.handbookPhase;
const rhythm = $json.handbookRhythm;

// Costruisci il front-matter YAML
const yamlFrontMatter = `---
title: "${title.replace(/"/g, '\"')}"
tags: [${tags.map(t => `"${t.replace(/"/g, '\"')}"`).join(', ')}]
phase: "${phase}"
rhythm: "${rhythm}"
---

`;

const finalMarkdownContent = yamlFrontMatter + proposedContent;

return [{ json: { ...$json, proposedContent: proposedContent, reviewTitle: title, reviewTags: tags, finalMarkdownContent: finalMarkdownContent } }];

// Genera un ID univoco per questa richiesta di revisione.
// Questo ID verrà usato per correlare la risposta del revisore con questa istanza del workflow.
const reviewId = crypto.randomUUID();

return [{ json: { ...$json, reviewId: reviewId } }];

// Registra il contributo dell'agente dopo l'approvazione umana
const entryId = $json.id; // ID dall'inserimento in handbook_entries
const agentName = 'Archivist';
const contributionType = 'Archiving (Approved)';
const details = { input: $json.proposedContent, metadata: { title: $json.reviewTitle, version: $json.version, tags: $json.reviewTags, phase: $json.handbookPhase, rhythm: $json.handbookRhythm }, reviewStatus: 'approved' };

$items[0].json.contribution = { entryId, agentName, contributionType, details };
return $items;

// Genera il percorso del file GitHub con slug e timestamp per il versioning.
const chapterSlug = ($json.reviewTitle || 'untitled').replace(/[^a-zA-Z0-9]/g, '-').toLowerCase();
const timestamp = new Date().toISOString().replace(/[:.-]/g, ''); // Rimuove caratteri non validi per i nomi di file
const filePathWithVersion = `content/${chapterSlug}_v${timestamp}.md`;

return [{ json: { ...$json, githubFilePath: filePathWithVersion } }];

// Registra il rifiuto umano del contenuto
const agentName = 'Archivist';
const reviewId = $json.reviewId;
const reviewStatus = 'rejected';
const reviewComments = $json.query.comments || 'Nessun commento fornito.';
const proposedContent = $json.proposedContent;
const title = $json.reviewTitle;

console.log(`Contenuto proposto dall'Archivista (ID: ${reviewId}, Titolo: ${title}) rifiutato dall'umano. Commenti: ${reviewComments}`);

// Prepara l'output per indicare il rifiuto e consentire al flusso di continuare.
return [{ json: { ...$json, reviewStatus: reviewStatus, reviewComments: reviewComments, output: { message: `Archivista: Contenuto rifiutato dall'umano.`, status: 'rejected', comments: reviewComments } } }];

// Valuta i flag 'major_issue' dagli agenti di revisione per determinare se è necessaria una rielaborazione.
let majorIssueCount = 0;
let redraftFeedback = '';

// Assumi che gli output degli agenti di revisione siano accessibili tramite $node
// (Ad esempio, se Peer Reviewer -> Sensemaking -> Prompt Engineer sono sequenziali prima di questo nodo)

if ($node["Peer Reviewer Agent"] && $node["Peer Reviewer Agent"].json && $node["Peer Reviewer Agent"].json.choices && $node["Peer Reviewer Agent"].json.choices[0] && $node["Peer Reviewer Agent"].json.choices[0].message && $node["Peer Reviewer Agent"].json.choices[0].message.content) {
    const peerReviewOutput = JSON.parse($node["Peer Reviewer Agent"].json.choices[0].message.content);
    if (peerReviewOutput.major_issue) majorIssueCount++;
    redraftFeedback += `Peer Reviewer: ${peerReviewOutput.suggestions || ''}\n`;
}

if ($node["Sensemaking Agent"] && $node["Sensemaking Agent"].json && $node["Sensemaking Agent"].json.choices && $node["Sensemaking Agent"].json.choices[0] && $node["Sensemaking Agent"].json.choices[0].message && $node["Sensemaking Agent"].json.choices[0].message.content) {
    const sensemakingOutput = JSON.parse($node["Sensemaking Agent"].json.choices[0].message.content);
    if (sensemakingOutput.major_issue) majorIssueCount++;
    redraftFeedback += `Sensemaking Agent: ${sensemakingOutput.suggestions || ''}\n`;
}

if ($node["Prompt Engineer Agent"] && $node["Prompt Engineer Agent"].json && $node["Prompt Engineer Agent"].json.choices && $node["Prompt Engineer Agent"].json.choices[0] && $node["Prompt Engineer Agent"].json.choices[0].message && $node["Prompt Engineer Agent"].json.choices[0].message.content) {
    const promptEngineerOutput = JSON.parse($node["Prompt Engineer Agent"].json.choices[0].message.content);
    if (promptEngineerOutput.major_issue) majorIssueCount++;
    redraftFeedback += `Prompt Engineer: ${promptEngineerOutput.suggestions || ''}\n`;
}

const redraftNeeded = majorIssueCount >= 2; // Voto a maggioranza

return [{ json: { ...$json, redraftNeeded: redraftNeeded, redraftFeedback: redraftFeedback } }];

// Gestisce la logica di rielaborazione: incrementa il contatore e reindirizza al Synthesizer.

$workflow.redraftLoopCount += 1; // Incrementa il contatore del ciclo di rielaborazione

// Trova l'indice del Synthesizer nella sequenza degli agenti
const synthesizerIndex = $workflow.agentSequence.indexOf("Synthesizer");

// Se il Synthesizer non è il prossimo agente, imposta l'indice corrente per farlo ripartire dal Synthesizer.
// Questo garantisce che il Synthesizer venga eseguito successivamente per la rielaborazione.
if ($workflow.currentAgentIndex !== synthesizerIndex) {
    $workflow.currentAgentIndex = synthesizerIndex;
} else {
    // Se siamo già sul Synthesizer (es. dopo il primo passaggio del loop), assicurati che l'indice vada avanti normalmente nel prossimo ciclo.
    // Questo è un caso limite, di solito il Prepare Agent Input lo gestirà.
}

// Passa il feedback di rielaborazione come input per il Synthesizer.
// Il nodo 'Prepare Agent Input' utilizzerà questo campo per aggiornare 'agentInput'.
return [{ json: { ...$json, redraftInput: $json.output + "\n\nFeedback per la rielaborazione dal Peer Review Board:\n" + $json.redraftFeedback } }];

// Ottieni il nome dell'agente appena eseguito
const agentName = $json.agentToRun;
let agentOutput = '';

// Costruisci dinamicamente il nome del nodo per il recupero dell'output
let actualNodeName;

// Gestione speciale per l'Archivista dopo la revisione umana
if (agentName === 'Archivist') {
    if ($json.query && $json.query.status === 'approved') {
        agentOutput = { message: 'Contenuto archiviato con successo dopo l'approvazione umana.', entryId: $json.id || 'N/A', handbookMetadata: { title: $json.reviewTitle, tags: $json.reviewTags, phase: $json.handbookPhase, rhythm: $json.handbookRhythm } };
    } else if ($json.query && $json.query.status === 'rejected') {
        agentOutput = { message: 'Archiviazione rifiutata dall'umano.', reviewComments: $json.query.comments || 'Nessun commento' };
    } else if ($json.reviewStatus === 'rejected') {
        // Caso in cui la revisione umana è stata rifiutata e si proviene dal ramo di rifiuto
        agentOutput = { message: 'Archiviazione rifiutata dall'umano (tramite ramo di rifiuto).', reviewComments: $json.reviewComments || 'Nessun commento' };
    } else {
        agentOutput = { message: 'Processo Archivista: Attesa revisione umana o stato inatteso.', status: 'pending_review' };
    }
} else {
    // Logica originale per gli altri agenti OpenAI
    if (agentName === 'Onboarding/Explainer') {
        actualNodeName = 'Onboarding/Explainer Agent';
    } else {
        actualNodeName = agentName + ' Agent';
    }

    // Tenta in modo sicuro di recuperare l'output dal nodo determinato dinamicamente
    // Tenta anche di analizzare il JSON se l'output dell'agente è un oggetto JSON (come per gli agenti di revisione)
    let rawContent = '';
    if ($node[actualNodeName] && $node[actualNodeName].json && $node[actualNodeName].json.choices && $node[actualNodeName].json.choices[0] && $node[actualNodeName].json.choices[0].message && $node[actualNodeName].json.choices[0].message.content) {
        rawContent = $node[actualNodeName].json.choices[0].message.content;
    } else {
        console.warn(`Impossibile trovare l'output chat OpenAI standard per il nodo: ${actualNodeName}. Ritorno al JSON grezzo.`);
        agentOutput = $node[actualNodeName] ? $node[actualNodeName].json : `Nessun output specifico trovato per ${agentName}`;
    }

    // Tenta di analizzare il contenuto come JSON se l'agente è un agente di revisione
    if (agentName === 'Peer Reviewer' || agentName === 'Sensemaking Agent' || agentName === 'Prompt Engineer') {
        try {
            agentOutput = JSON.parse(rawContent);
        } catch (e) {
            console.warn(`Impossibile analizzare l'output di ${agentName} come JSON. Trattato come stringa.`);
            agentOutput = rawContent;
        }
    } else {
        agentOutput = rawContent;
    }
}

// Registra il contributo
const contribution = {
    agent: agentName,
    output: agentOutput,
    timestamp: new Date().toISOString()
};

// Assicurati che l'array dei contributi esista e aggiungi il nuovo contributo
const existingContributions = Array.isArray($json.contributions) ? $json.contributions : [];

// Incrementa l'indice dell'agente per il loop (solo se non siamo in rielaborazione e questo non è un agente di revisione che porta a rielaborazione)
// Questo è gestito dalla logica di 'Handle Redraft' che forza l'indice per il riavvio.
if (!($json.redraftNeeded && $workflow.redraftLoopCount < 2 && agentName !== 'Synthesizer')) { // Evita doppio incremento se riavvia il Synthesizer
    $workflow.currentAgentIndex += 1;
}

// Restituisce l'elemento aggiornato, preservando l'input originale, aggiungendo l'output corrente e tutti i contributi
return [{ json: { ...$items[0].json, output: agentOutput, contributions: [...existingContributions, contribution] } }];

