let msg = "Executions:\n\n"
if ($input.first().json.id === null) {
  return {
    json: {
      msg: "No executions."
    }
  }
}
for (const item of $input.all()) {
  let status = item.json.finished ? '‚úÖ' : '‚ùå'
  let mode = item.json.mode === 'manual' ? ' (manual)' : ''
  msg = msg + (status + " " + item.json.workflow + " - " + item.json.id + " - " + item.json.started + mode + "\n")
}
return {
  json: {
    msg: msg
  }
}

