for (const [category, entries] of Object.entries(riskData)) {
  if (entries.length === 0) continue;
  const { color, emoji } = categoryStyles[category];
  html += `<div class="risk-YOUR_OPENAI_KEY_HERE" style="background-color: ${color};">${emoji} ${category} Risk</div>`;
  entries.forEach(item => {
    html += `
      <div class="card" style="border-left-color: ${color};">
        <div class="vendor">${item.vendor || '‚Äî'}</div>
        <div class="title">${item.title || '‚Äî'}</div>
        <div class="summary">${item.summary}</div>
        <div class="read-more"><a href="${item.link}" target="_blank" rel="noopener noreferrer">Read more</a></div>
      </div>
    `;
  });
}

html += `</body></html>`;

return [{ json: { html } }];

// Build an array of { link, content } pairs
const entries = items.map(item => ({
  link:    item.json.link,
  content: item.json.contentSnippet || item.json.content || ""
}));

// Return one item with a single field ‚Äúentries‚Äù
return [
  {
