// Normalize incoming messages from different channels
const items = [];

for (const item of $input.all()) {
  const source = item.json;

  let normalizedTicket = {
    id: 'TKT-' + Date.now() + '-' + Math.random().toString(36).substr(2, 5),
    timestamp: new Date().toISOString(),
    channel: '',
    customer: {
      name: '',
      email: '',
      id: ''
    },
    message: {
      subject: '',
      body: '',
      attachments: []
    },
    metadata: {},
    sourceData: source
  };

  // Check if this is from Email Trigger
  if (source.from && source.subject) {
    normalizedTicket.channel = 'email';
    normalizedTicket.customer.email = source.from.value?.[0]?.address || source.from;
    normalizedTicket.customer.name = source.from.value?.[0]?.name || 'Email Customer';
    normalizedTicket.message.subject = source.subject;
    normalizedTicket.message.body = source.text || source.html || '';
    if (source.attachments) {
      normalizedTicket.message.attachments = source.attachments.map(att => ({
        filename: att.filename,
        size: att.size
      }));
    }
  }
  // Check if this is from Web Form
  else if (source.body && (source.body.email || source.body.name)) {
    normalizedTicket.channel = 'webform';
    normalizedTicket.customer.name = source.body.name || 'Web Form User';
    normalizedTicket.customer.email = source.body.email || '';
    normalizedTicket.message.subject = source.body.subject || 'Web Form Submission';
    normalizedTicket.message.body = source.body.message || source.body.description || '';
  }
  // Generic webhook data
  else {
    normalizedTicket.channel = 'webhook';
    normalizedTicket.customer.name = source.name || source.customerName || 'Unknown';
    normalizedTicket.customer.email = source.email || source.customerEmail || '';
    normalizedTicket.message.subject = source.subject || 'Support Request';
    normalizedTicket.message.body = source.message || source.body || JSON.stringify(source);
  }

  items.push({
    json: normalizedTicket
  });
}

return items;

// Categorize and prioritize tickets based on content
const items = [];

for (const item of $input.all()) {
  const ticket = item.json;
  const message = (ticket.message.body + ' ' + ticket.message.subject).toLowerCase();

  let category = 'general';
  let priority = 'medium';
  let tags = [];
  let sentiment = 'neutral';

  // Category detection rules
  const categoryRules = {
    'billing': ['invoice', 'payment', 'charge', 'refund', 'subscription', 'cancel', 'upgrade', 'downgrade', 'pricing', 'cost', 'bill'],
    'technical': ['bug', 'error', 'broken', 'not working', 'crash', 'slow', 'issue', 'problem', 'fix', '500', '404', 'loading'],
    'account': ['password', 'login', 'access', 'account', 'email', 'username', '2fa', 'locked', 'forgot', 'reset', 'sign in'],
    'feature': ['feature', 'request', 'suggestion', 'improvement', 'add', 'would be nice', 'enhancement', 'idea'],
    'complaint': ['terrible', 'awful', 'worst', 'hate', 'angry', 'frustrated', 'disappointed', 'unacceptable', 'ridiculous']
  };

  // Check categories
  for (const [cat, keywords] of Object.entries(categoryRules)) {
    if (keywords.some(keyword => message.includes(keyword))) {
      category = cat;
      tags.push(cat);
      break;
    }
  }

  // Sentiment analysis (simplified)
  const negativeWords = ['not working', 'broken', 'error', 'angry', 'frustrated', 'terrible', 'awful', 'worst', 'hate', 'disappointed', 'unacceptable'];
  const positiveWords = ['thank', 'great', 'excellent', 'love', 'amazing', 'fantastic', 'wonderful', 'perfect'];
  const urgentWords = ['urgent', 'asap', 'emergency', 'critical', 'immediately', 'now', 'quickly'];

  const negativeCount = negativeWords.filter(word => message.includes(word)).length;
  const positiveCount = positiveWords.filter(word => message.includes(word)).length;

  if (negativeCount > positiveCount + 1) {
    sentiment = 'negative';
    tags.push('negative_sentiment');
  } else if (positiveCount > negativeCount + 1) {
    sentiment = 'positive';
    tags.push('positive_sentiment');
  }

  // Priority rules
  if (urgentWords.some(word => message.includes(word))) {
    priority = 'urgent';
    tags.push('urgent');
  } else if (category === 'complaint' || sentiment === 'negative') {
    priority = 'high';
  } else if (category === 'billing' || category === 'account') {
    priority = 'high';
  }

  // Check for VIP customers (you can add your VIP domains here)
  const vipDomains = ['enterprise.com', 'vip.com', 'premium.com'];
  if (ticket.customer.email && vipDomains.some(domain => ticket.customer.email.includes(domain))) {
    priority = priority === 'medium' ? 'high' : priority;
    tags.push('vip_customer');
  }

  items.push({
    json: {
      ...ticket,
      category,
      priority,
      tags,
      sentiment,
      requiresHumanReview: priority === 'urgent' || sentiment === 'negative',
      autoResponseEligible: priority !== 'urgent' && sentiment !== 'negative' && category !== 'complaint'
    }
  });
}

return items;

// Generate auto-response based on category
const items = [];

for (const item of $input.all()) {
  const ticket = item.json;

  const templates = {
    'billing': {
      subject: `Re: Your billing inquiry - ${ticket.id}`,
      body: `Hello ${ticket.customer.name || 'Valued Customer'},

Thank you for contacting us about your billing concern. We understand how important this is.

Your ticket (${ticket.id}) has been received and forwarded to our billing department. You can expect a response within 2-4 business hours.

In the meantime, you might find these resources helpful:
- Billing FAQ: https://support.yourcompany.com/billing
- View invoices: https://app.yourcompany.com/billing

Best regards,
Customer Support Team`
    },
    'technical': {
      subject: `Re: Technical support request - ${ticket.id}`,
      body: `Hello ${ticket.customer.name || 'Valued Customer'},

We've received your technical support request (${ticket.id}).

Our technical team is reviewing your issue. To help us resolve this faster, please ensure you've provided:
- Description of the issue
- Steps to reproduce
- Any error messages

Expected response time: Within 4-6 hours

Best regards,
Technical Support Team`
    },
    'account': {
      subject: `Re: Account assistance - ${ticket.id}`,
      body: `Hello ${ticket.customer.name || 'Valued Customer'},

We've received your account-related request (${ticket.id}).

For security reasons, our account specialists will handle this personally. You'll hear from us within 1-2 business hours.

Best regards,
Account Security Team`
    },
    'feature': {
      subject: `Re: Feature request received - ${ticket.id}`,
      body: `Hello ${ticket.customer.name || 'Valued Customer'},

Thank you for your feature suggestion! We love hearing from our users.

Your request (${ticket.id}) has been logged and will be reviewed by our product team. We consider all feedback when planning future updates.

Best regards,
Product Team`
    },
    'general': {
      subject: `Re: ${ticket.message.subject} - ${ticket.id}`,
      body: `Hello ${ticket.customer.name || 'Valued Customer'},

Thank you for contacting support. We've received your request (${ticket.id}) and our team is reviewing it.

We'll get back to you within 24 business hours.

Best regards,
Customer Support Team`
    }
  };

  const template = templates[ticket.category] || templates['general'];

  items.push({
    json: {
      ...ticket,
      autoResponse: {
        to: ticket.customer.email,
        subject: template.subject,
        body: template.body,
        shouldSend: !!ticket.customer.email && ticket.autoResponseEligible
      }
    }
  });
}

return items;

// Store ticket in database or CRM
// This is a placeholder - replace with your actual CRM node (Zendesk, HubSpot, etc.)

const items = [];

for (const item of $input.all()) {
  const ticket = item.json;

  // Example structure for CRM integration
  const crmTicket = {
    external_id: ticket.id,
    subject: ticket.message.subject || `Support Request from ${ticket.channel}`,
    description: ticket.message.body,
    priority: ticket.priority,
    status: 'new',
    channel: ticket.channel,
    tags: ticket.tags,
    custom_fields: {
      sentiment: ticket.sentiment,
      category: ticket.category,
      auto_response_sent: ticket.autoResponseEligible
    },
    requester: {
      name: ticket.customer.name,
      email: ticket.customer.email
    },
    created_at: ticket.timestamp
  };

  // In production, you would send this to your CRM API
  console.log('Would create ticket in CRM:', crmTicket);

  items.push({
    json: {
      ...ticket,
      crmTicket,
      stored: true
    }
  });
}

return items;

// Log success metrics
const items = [];

for (const item of $input.all()) {
  const ticket = item.json;

  const metrics = {
    timestamp: new Date().toISOString(),
    ticketId: ticket.id,
    channel: ticket.channel,
    category: ticket.category,
    priority: ticket.priority,
    sentiment: ticket.sentiment,
    autoResponseSent: ticket.autoResponseEligible,
    processingTime: Date.now() - new Date(ticket.timestamp).getTime(),
    success: true
  };

  // Log to console (in production, send to analytics platform)
  console.log('Ticket processed successfully:', metrics);

  items.push({ json: metrics });
}

return items;

// Handle errors and log them
const error = $json["error"];
const originalData = $json["originalData"] || {};

const errorLog = {
  timestamp: new Date().toISOString(),
  workflow: 'customer-support-automation',
  error: {
    message: error?.message || 'Unknown error',
    code: error?.code,
    node: error?.node
  },
  ticketId: originalData.id || 'unknown',
  channel: originalData.channel || 'unknown',
  recovery: 'manual_intervention_required'
};

// In production, send to error tracking service (Sentry, etc.)
console.error('Workflow error:', errorLog);

// Could also send critical errors to Slack
return [{ json: errorLog }];
