// --- Configuration ---
const REMOVE_EMPTY_VALUES = true; // Omit empty tags

// Newline configuration (number of '\n')
const NEWLINES_TOP_LEVEL = 2;         // Between top-level elements (e.g., 2 for \n\n)
const NEWLINES_ARRAY_ITEMS = 2;       // Between <0>, <1>... elements of a complex array
const NEWLINES_OBJECT_PROPERTIES = 1; // Between <key1>, <key2>... properties of an object
const NEWLINES_WITHIN_TAGS = 1;       // Between opening/closing tag and its content (0, 1, 2...)

// --- Input Retrieval ---
const inputJson = $input.item.json;

// --- Utility Functions ---

/**
 * Generates a string containing the specified number of newlines.
 * @param {number} count The desired number of newlines.
 * @returns {string} The newline string (e.g., "\n", "\n\n").
 */
function generateNewlines(count) {
  return '\n'.repeat(Math.max(0, count)); // Ensures a non-negative count
}

/**
 * Attempts to detect and format a value into a readable date (YYYY-MM-DD HH:mm:ss).
 * @param {any} value The value to check and format.
 * @returns {string|any} The formatted date if detected, otherwise the original value.
 */
function formatIfDate(value) {
  const originalValue = value;
  let date = null;
  try {
    if (value instanceof Date && !isNaN(value.getTime())) {
      date = value;
    }
    /* // Numeric timestamp detection (COMMENTED OUT)
    else if (typeof value === 'number') { ... } */
    else if (typeof value === 'string') {
      // Basic check for string length and presence of digits/date separators
      if (value.length >= 8 && /[0-9]/.test(value) && /[-/T:.\s]/.test(value)) {
        const parsedDate = new Date(value);
        if (!isNaN(parsedDate.getTime())) {
          date = parsedDate;
        }
      }
    }
    if (date instanceof Date && !isNaN(date.getTime())) {
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      const hours = String(date.getHours()).padStart(2, '0');
      const minutes = String(date.getMinutes()).padStart(2, '0');
      const seconds = String(date.getSeconds()).padStart(2, '0');
      return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
    }
  } catch (e) {
    console.warn(`Error formatting date: ${value}`, e);
    return originalValue;
  }
  return originalValue;
}


/**
 * Finds a safe delimiter to join elements of a primitive array.
 * @param {Array<any>} arr The array whose elements will be joined.
 * @returns {string} The found delimiter.
 */
function findArrayDelimiter(arr) {
  const stringElements = arr.map(el => String(el === null || el === undefined ? '' : el));
  let delimiters = [',', '|'];
  // Try common delimiters first
  for (const d of delimiters) {
    if (!stringElements.some(s => s.includes(d))) {
      return d;
    }
  }
  // If common delimiters are found in elements, create a unique one
  let currentDelimiter = '||';
  while (stringElements.some(s => s.includes(currentDelimiter))) {
    currentDelimiter += '|';
    if (currentDelimiter.length > 20) {
      console.warn("Long delimiter used:", arr);
      return currentDelimiter; // Prevent infinite loop for extremely complex cases
    }
  }
  return currentDelimiter;
}

/**
 * Checks if a value is a primitive (string, number, boolean, null, undefined).
 * @param {any} value The value to check.
 * @returns {boolean} True if it's a primitive.
 */
function isPrimitive(value) {
  const type = typeof value;
  return value === null || type === 'undefined' || type === 'string' || type === 'number' || type === 'boolean';
}


/**
 * Recursive function to convert a JSON value into an XML fragment.
 * Uses configuration parameters for newlines.
 * Does NOT use XML escaping.
 * @param {any} value The JSON value to convert.
 * @param {string} tagName The XML tag name.
 * @returns {string} The generated XML fragment, or an empty string if the value is empty and should be omitted.
 */
function jsonToXmlRecursive(value, tagName) {
  const openTag = `<${tagName}>`;
  const closeTag = `</${tagName}>`;

  let processedValue = value;

  // 1. Attempt to parse JSON/Array strings
  if (typeof value === 'string') {
    const trimmedValue = value.trim();
    if (trimmedValue.startsWith('{') || trimmedValue.startsWith('[')) {
      try {
        processedValue = JSON.parse(trimmedValue);
        console.log(`String parsed for tag: '${tagName}'.`);
      } catch (e) {
        processedValue = value; // Keep the original string if parsing fails
        console.warn(`Parsing failed for tag: '${tagName}'. Error: ${e.message}`);
      }
    } else {
      processedValue = trimmedValue; // Use the trimmed version for further processing
    }
  }

  // 2. Initial check for null/undefined values
  if (processedValue === null || processedValue === undefined) {
    return REMOVE_EMPTY_VALUES ? '' : `${openTag}${closeTag}`;
  }

  // 3. Process based on type and calculate innerContent
  let innerContent = '';
  const nlArrayItemSep = generateNewlines(NEWLINES_ARRAY_ITEMS);
  const nlObjectPropSep = generateNewlines(NEWLINES_OBJECT_PROPERTIES);

  if (Array.isArray(processedValue)) {
    if (processedValue.length === 0) {
      return REMOVE_EMPTY_VALUES ? '' : `${openTag}${closeTag}`;
    }

    const elementsAfterDateFormatting = processedValue.map(el => formatIfDate(el));
    const allPrimitives = elementsAfterDateFormatting.every(el => isPrimitive(el));

    if (allPrimitives) { // Array of primitives -> join
      const delimiter = findArrayDelimiter(elementsAfterDateFormatting);
      const joinedString = elementsAfterDateFormatting
        .map(el => String(el === null || el === undefined ? '' : el))
        .join(delimiter)
        .trim();
      if (REMOVE_EMPTY_VALUES && joinedString === '') return '';
      innerContent = joinedString;
    } else { // Complex/mixed array -> iterate and join with configured separator
      const innerParts = [];
      processedValue.forEach((item, index) => {
        const itemXml = jsonToXmlRecursive(item, String(index)); // Use index as tag name for array items
        if (itemXml !== '') innerParts.push(itemXml);
      });
      if (REMOVE_EMPTY_VALUES && innerParts.length === 0) return '';
      innerContent = innerParts.join(nlArrayItemSep); // Uses array item separator
    }
  } else if (typeof processedValue === 'object') {
    if (Object.keys(processedValue).length === 0) {
      return REMOVE_EMPTY_VALUES ? '' : `${openTag}${closeTag}`;
    }
    // Object -> iterate and join with configured separator
    const innerParts = [];
    for (const key in processedValue) {
      if (Object.prototype.hasOwnProperty.call(processedValue, key)) {
        const propertyXml = jsonToXmlRecursive(processedValue[key], key);
        if (propertyXml !== '') innerParts.push(propertyXml);
      }
    }
    if (REMOVE_EMPTY_VALUES && innerParts.length === 0) return '';
    innerContent = innerParts.join(nlObjectPropSep); // Uses object property separator
  } else { // Primitive
    const finalValue = formatIfDate(processedValue);
    const stringValue = String(finalValue === null || finalValue === undefined ? '' : finalValue).trim();
    if (REMOVE_EMPTY_VALUES && stringValue === '') return '';
    innerContent = stringValue;
  }

  // 4. Construct the final XML for this value WITH configured newlines
  const nlWithin = generateNewlines(NEWLINES_WITHIN_TAGS);
  if (innerContent === '') {
    // If content is empty, no internal newlines
    return `${openTag}${closeTag}`;
  } else {
    // If there is content, add the configured internal newlines
    return `${openTag}${nlWithin}${innerContent}${nlWithin}${closeTag}`;
  }
}

// --- Main Conversion Logic ---

let finalXmlParts = [];
const nlTopLevelSep = generateNewlines(NEWLINES_TOP_LEVEL); // Separator for level 1

if (inputJson && typeof inputJson === 'object' && !Array.isArray(inputJson)) {
  // If the input is a direct object, iterate its properties as top-level elements
  for (const topLevelKey in inputJson) {
    if (Object.prototype.hasOwnProperty.call(inputJson, topLevelKey)) {
      const part = jsonToXmlRecursive(inputJson[topLevelKey], topLevelKey);
      if (part !== '') finalXmlParts.push(part);
    }
  }
} else if (inputJson !== null && inputJson !== undefined) {
  // If the input is a primitive or an array, wrap it in a <root> tag
  console.warn("Non-object input. Encapsulating in <root>.");
  const part = jsonToXmlRecursive(inputJson, "root");
  if (part !== '') {
    finalXmlParts.push(part);
  } else {
    console.warn("Entire input is empty and omitted.");
  }
} else {
  console.warn("Empty/invalid JSON input.");
}

// Join the top-level XML fragments with the configured separator
const finalXmlString = finalXmlParts.join(nlTopLevelSep);

// --- Output Preparation ---
return {
  xml: finalXmlString
};
