parameters.inputSchema:
{
  "type": "object",
  "required": ["category", "urgency", "sentiment", "keywords", "productIdentifiers", "customerInfo", "requiredAction"],
  "properties": {
    "category": {
      "type": "string",
      "enum": ["technical", "billing", "sales", "returns", "general"],
      "description": "The primary category of the customer request"
    },
    "urgency": {
      "type": "string",
      "enum": ["low", "medium", "high", "critical"],
      "description": "How time-sensitive the request is"
    },
    "sentiment": {
      "type": "string",
      "enum": ["positive", "neutral", "negative", "very_negative"],
      "description": "The emotional tone of the customer's message"
    },
    "keywords": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Key terms extracted from the request for searching knowledge base and ERP"
    },
    "productIdentifiers": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "enum": ["model", "serial", "part"]
          },
          "value": {
            "type": "string"
          }
        }
      },
      "description": "Any product numbers, models, or parts mentioned in the request"
    },
    "customerInfo": {
      "type": "object",
      "properties": {
        "firstName": {
          "type": "string"
        },
        "lastName": {
          "type": "string"
        },
        "customerId": {
          "type": "string",
          "description": "CRM Customer Id"
        },
        "mentionsCompetitor": {
          "type": "boolean",
          "description": "Customer references a competitor product or service"
        }
      }
    },
    "requiredAction": {
      "type": "string",
      "enum": ["information", "replacement", "refund", "repair", "callback", "escalation"],
      "description": "The primary action needed to resolve the customer's request"
    }
  }}
