parameters.jsCode:
// Access inputs: HTTP data (first input) and potential AlienVault data (second input)
const httpData = $input.first()?.json || {}; // Assuming HTTP Request is the first input
const alienVaultInput = $input.all().length > 1 ? $input.all()[1] : null; // Check if a second input exists
const alienVaultData = alienVaultInput?.json || {};

// Initialize the consolidated data object. This will be the input for the "Security Configuration Audit" node.
const consolidatedInputData = {
  url: httpData.url || alienVaultData.indicator || 'URL not provided',
  statusCode: httpData.statusCode || null,
  securityHeaders: httpData.headers || {}, // Keep original headers
  potentialIssues: [], // This will be augmented by the AI/Security Audit node
  alienVaultInfo: {
    domain: alienVaultData.domain || null,
    sections: alienVaultData.sections || [],
    whois: alienVaultData.whois || null,
    pulseCount: alienVaultData.pulse_info?.count || 0
  },
  timestamp: new Date().toISOString(), // Add a timestamp for the report generation time
  // Add raw inputs for debugging/reference (useful if you need to debug data flow)
  _rawHttpInput: httpData,
  _rawAlienVaultInput: alienVaultData
};

// Check for potential issues based on AlienVault data
if (consolidatedInputData.alienVaultInfo.sections && consolidatedInputData.alienVaultInfo.sections.includes('malware')) {
  consolidatedInputData.potentialIssues.push('Malware section detected in AlienVault data; further analysis recommended');
}
if (consolidatedInputData.alienVaultInfo.pulseCount > 0) {
  consolidatedInputData.potentialIssues.push('Referenced in AlienVault pulses, indicating potential threats');
}

// Check for potential issues based on HTTP data
if (consolidatedInputData.statusCode && consolidatedInputData.statusCode >= 400) {
  consolidatedInputData.potentialIssues.push(`Server returned error status: ${consolidatedInputData.statusCode}`);
}
// Check if securityHeaders is an object and if it's empty
if (consolidatedInputData.securityHeaders && typeof consolidatedInputData.securityHeaders === 'object' && Object.keys(consolidatedInputData.securityHeaders).length === 0) {
  consolidatedInputData.potentialIssues.push('No security headers detected');
}

// Return a single item with the consolidated input data for the next node (Security Configuration Audit)
return [
  {
    json: consolidatedInputData
  }
];

const aiReport = $input.first()?.json?.output; // Assuming AI output is under 'output' property in the first input item

if (!aiReport) {
  return [{ json: { htmlReport: "<p>No AI report generated.</p>" } }];
}

// Basic HTML formatting. You can make this much more sophisticated.
// The AI's output is likely plain text, so we'll wrap it in <pre> or simple <p> tags.
// If the AI is instructed to output Markdown, you could use a Markdown-to-HTML library here
// (though n8n's Code node doesn't easily support external libraries).
// For simplicity, let's assume plain text and wrap it.

let htmlContent = `
<html>
<head>
  <style>
    body { font-family: sans-serif; line-height: 1.6; color: #333; }
    h1, h2 { color: #0056b3; }
    pre { background-color: #f4f4f4; border: 1px solid #ddd; padding: 10px; border-radius: 4px; overflow-x: auto; }
    .issue { color: #cc0000; font-weight: bold; }
    .section-title { font-size: 1.2em; margin-top: 20px; border-bottom: 1px solid #eee; padding-bottom: 5px; }
  </style>
</head>
<body>
  <h1>Website Security Audit Report</h1>
  <div class="section-title">AI-Generated Analysis:</div>
  <pre>${aiReport}</pre>
  <div class="section-title">Raw Data for Reference:</div>
  <p>This is placeholder for raw data. You can expand on this section.</p>
  </body>
</html>
`;

return [{
  json: {
    htmlReport: htmlContent
  }
}];
