async function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

// ‚è±Ô∏è 1 Minute = 60000 ms
await sleep(60000);

return [
  {
    json: {
      message: "1 minute wait done",
      originalInput: $json["message"] || "No input"
    }
  }
];

const results = [];

for (const item of $input.all()) {
  const rawOutput = item.json.output;

  if (!rawOutput) continue;

  const records = rawOutput.split(/\*\*Pin \d+:\*\*/g);

  for (let i = 1; i < records.length; i++) {
    const recordText = records[i];
    if (recordText.trim() === '') continue;

    const extractedItem = {};

    // Extract fields using updated regex
    const urlMatch = recordText.match(/- \*\*URL:\*\* (.*)/);
    if (urlMatch) extractedItem.URL = urlMatch[1].trim();

    const postIdMatch = recordText.match(/- \*\*Post ID:\*\* (.*)/);
    if (postIdMatch) extractedItem.PostID = postIdMatch[1].trim();

    const titleMatch = recordText.match(/- \*\*Title:\*\* (.*)/);
    if (titleMatch) extractedItem.Title = titleMatch[1].trim();

    const contentMatch = recordText.match(/- \*\*Content:\*\* (.*)/);
    if (contentMatch) extractedItem.Content = contentMatch[1].trim();

    const datePostedMatch = recordText.match(/- \*\*Date Posted:\*\* (.*)/);
    if (datePostedMatch) extractedItem.DatePosted = datePostedMatch[1].trim();

    const userMatch = recordText.match(/- \*\*User:\*\* (.*)/);
    if (userMatch) extractedItem.User = userMatch[1].trim();

    const engagementMatch = recordText.match(/- \*\*Engagement:\*\* (\d+) likes, (\d+) comment/);
    if (engagementMatch) {
      extractedItem.Likes = parseInt(engagementMatch[1], 10);
      extractedItem.Comments = parseInt(engagementMatch[2], 10);
    }

    const mediaMatch = recordText.match(/- \*\*Media:\*\* (.*)/);
    if (mediaMatch) extractedItem.Media = mediaMatch[1].trim();

    const imageUrlMatch = recordText.match(/- \*\*Image URL:\*\* (.*)/);
    if (imageUrlMatch) extractedItem.ImageURL = imageUrlMatch[1].trim();

    const categoriesMatch = recordText.match(/- \*\*Categories:\*\* (.*)/);
    if (categoriesMatch) {
      extractedItem.Categories = categoriesMatch[1].split(',').map(s => s.trim());
    }

    const hashtagsMatch = recordText.match(/- \*\*Hashtags:\*\* (.*)/);
    if (hashtagsMatch) {
      extractedItem.Hashtags = hashtagsMatch[1].split(',').map(s => s.trim());
    }

    const commentMatch = recordText.match(/- \*\*Comment:\*\* (.*)/);
    if (commentMatch) extractedItem.Comment = commentMatch[1].trim();

    const commentsSectionMatch = recordText.match(/- \*\*Comments:\*\* (.*)/);
    if (commentsSectionMatch) {
      extractedItem.CommentsText = commentsSectionMatch[1].split(',').map(s => s.trim());
    }

    if (Object.keys(extractedItem).length > 0) {
      results.push({ json: extractedItem });
    }
  }
}

return results;
