// Normalize input data from different sources
const inputData = $input.first().json;
let messageText = '';
let sourceType = '';
let sourceId = '';
let timestamp = new Date().toISOString();

// Handle WhatsApp input
if (inputData.messages) {
  messageText = inputData.messages[0]?.text?.body || '';
  sourceType = 'whatsapp';
  sourceId = inputData.messages[0]?.from || 'unknown';
} 
// Handle Email input
else if (inputData.subject || inputData.text) {
  messageText = inputData.text || inputData.subject || '';
  sourceType = 'email';
  sourceId = inputData.from || 'unknown';
}
// Handle direct text input
else if (inputData.text) {
  messageText = inputData.text;
  sourceType = 'direct';
  sourceId = 'system';
} else {
  messageText = JSON.stringify(inputData);
  sourceType = 'unknown';
  sourceId = 'system';
}

// Basic validation
if (!messageText || messageText.trim().length === 0) {
  throw new Error('No message content found in input');
}

if (messageText.length > 5000) {
  messageText = messageText.substring(0, 5000) + '... [truncated]';
}

return {
  json: {
    originalMessage: messageText,
    sourceType: sourceType,
    sourceId: sourceId,
    timestamp: timestamp,
    messageLength: messageText.length
  }
};

// Enhanced summarization with multiple algorithms
const input = $input.first().json;
const detectedLang = input.detectedSourceLanguage || 'unknown';
const translatedText = input.translatedText || input.originalMessage;
const sourceType = $('Data Normalizer & Validator').first().json.sourceType;
const sourceId = $('Data Normalizer & Validator').first().json.sourceId;
const timestamp = $('Data Normalizer & Validator').first().json.timestamp;

// Advanced summarization function
function generateSummary(text, maxLength = 150) {
  if (!text || text.length <= maxLength) return text;

  // Extract key information
  const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);

  if (sentences.length <= 2) {
    return text.substring(0, maxLength) + (text.length > maxLength ? '...' : '');
  }

  // Prioritize first and last sentences, plus any with keywords
  const keywords = ['urgent', 'help', 'problem', 'issue', 'support', 'error', 'bug', 'complaint'];
  const importantSentences = [];

  // Always include first sentence
  if (sentences[0]) importantSentences.push(sentences[0].trim());

  // Add sentences with keywords
  sentences.forEach(sentence => {
    if (keywords.some(keyword => sentence.toLowerCase().includes(keyword))) {
      if (!importantSentences.includes(sentence.trim())) {
        importantSentences.push(sentence.trim());
      }
    }
  });

  // Add last sentence if different from first
  const lastSentence = sentences[sentences.length - 1].trim();
  if (lastSentence !== sentences[0].trim() && !importantSentences.includes(lastSentence)) {
    importantSentences.push(lastSentence);
  }

  let summary = importantSentences.join('. ');

  if (summary.length > maxLength) {
    summary = summary.substring(0, maxLength - 3) + '...';
  }

  return summary;
}

// Determine priority based on content
function getPriority(text) {
  const urgentKeywords = ['urgent', 'emergency', 'asap', 'immediately', 'critical'];
  const highKeywords = ['problem', 'issue', 'error', 'bug', 'complaint', 'help'];

  const lowerText = text.toLowerCase();

  if (urgentKeywords.some(keyword => lowerText.includes(keyword))) {
    return 'HIGH';
  } else if (highKeywords.some(keyword => lowerText.includes(keyword))) {
    return 'MEDIUM';
  }
  return 'LOW';
}

// Generate enhanced summary
const summary = generateSummary(translatedText);
const priority = getPriority(translatedText);
const wordCount = translatedText.split(/\s+/).length;

// Prepare structured output
const processedData = {
  // Core data
  originalLanguage: detectedLang,
  translatedText: translatedText,
  summary: summary,
  priority: priority,

  // Metadata
  sourceType: sourceType,
  sourceId: sourceId,
  timestamp: timestamp,
  wordCount: wordCount,

  // For notifications
  emailSubject: `[${priority}] Customer Message from ${sourceType.toUpperCase()} - ${detectedLang.toUpperCase()}`,
  notificationText: `Priority: ${priority}\nSource: ${sourceType.toUpperCase()}\nLanguage: ${detectedLang.toUpperCase()}\nSummary: ${summary}`,

  // For logging
  logEntry: {
    timestamp: timestamp,
    source: sourceType,
    sourceId: sourceId,
    originalLanguage: detectedLang,
    priority: priority,
    wordCount: wordCount,
    summary: summary
  }
};

return { json: processedData };

// Final completion and metrics calculation
const startTime = new Date($('Data Normalizer & Validator').first().json.timestamp);
const endTime = new Date();
const processingTime = endTime - startTime;

const metrics = {
  processingTimeMs: processingTime,
  totalStepsCompleted: 8,
  sourceType: $('Enhanced Summary & Priority Processor').first().json.sourceType,
  priority: $('Enhanced Summary & Priority Processor').first().json.priority,
  originalLanguage: $('Enhanced Summary & Priority Processor').first().json.originalLanguage,
  wordCount: $('Enhanced Summary & Priority Processor').first().json.wordCount,
  timestamp: endTime.toISOString(),
  status: 'COMPLETED',
  workflowVersion: '2.0'
};

console.log('Workflow completed successfully:', metrics);

return {
  json: {
    success: true,
    message: 'Multi-language customer support workflow completed successfully',
    metrics: metrics,
    summary: `Processed ${metrics.sourceType} message in ${metrics.originalLanguage}, translated to English, priority: ${metrics.priority}, completed in ${processingTime}ms`
  }
};
