const data = items[0].json;

// Extract arrays
const titles = data.Title || [];
const locations = data.Location || [];
const audiences = data.Audience || [];
const rawTimes = data.Time || [];

// Step 1: Remove invalid "time" placeholders
const invalidTimeLabels = ["Date/Time", "Title/Description", "Location", "Audience"];
const times = rawTimes.filter(time => !invalidTimeLabels.includes(time.trim()));

// Step 2: Safely calculate number of valid events
const eventCount = Math.min(titles.length, locations.length, audiences.length, times.length);

// Helper: Convert "Today @ 10 AM" ‚Üí ISO string with timezone
function parseTimeToISO(rawTime) {
  const match = rawTime.match(/@ ([0-9]{1,2})(?::([0-9]{2}))?\s?(AM|PM)/i);
  const now = new Date();

  if (!match) return null;

  let hour = parseInt(match[1]);
  const minute = match[2] ? parseInt(match[2]) : 0;
  const meridian = match[3].toUpperCase();

  if (meridian === "PM" && hour !== 12) hour += 12;
  if (meridian === "AM" && hour === 12) hour = 0;

  const start = new Date(now.getFullYear(), now.getMonth(), now.getDate(), hour, minute);
  const end = new Date(start.getTime() + 60 * 60 * 1000); // 1-hour event

  const offsetMinutes = start.getTimezoneOffset();
  const offsetHours = Math.floor(Math.abs(offsetMinutes) / 60);
  const offsetMins = Math.abs(offsetMinutes) % 60;
  const offsetSign = offsetMinutes > 0 ? "-" : "+";
  const offset = `${offsetSign}${String(offsetHours).padStart(2, "0")}:${String(offsetMins).padStart(2, "0")}`;

  const toISOStringWithOffset = (d) => {
    const pad = (n) => n.toString().padStart(2, "0");
    return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}T${pad(d.getHours())}:${pad(d.getMinutes())}:00${offset}`;
  };

  return {
    start: toISOStringWithOffset(start),
    end: toISOStringWithOffset(end),
  };
}

// Step 3: Build cleaned and enriched event objects
const results = [];

for (let i = 0; i < eventCount; i++) {
  const titleText = titles[i];
  const timeText = times[i]?.trim();
  const parsedTime = parseTimeToISO(timeText);

  results.push({
    json: {
      title: titleText.split('\n')[0]?.trim(),
      description: titleText.trim(),
      location: locations[i]?.trim(),
      audience: audiences[i]?.trim(),
      time: timeText,
      start: { dateTime: parsedTime?.start || null },
      end: { dateTime: parsedTime?.end || null },
      sourceUrl: "https://www.nypl.org" + (titleText.match(/\[([^\]]+)\]/)?.[1] || '')
    }
  });
}

return results;
