// Get the input data from the previous node
const inputData = items[0].json;

// Access the arrays from the input object
const titles = inputData.Title;
const dates = inputData['Date and Time'];

// Use a Set to track titles we've already processed to remove duplicates
const seenTitles = new Set();
const cleanedEvents = [];

// Get the number of actual events (the input has duplicates)
const eventCount = dates.length;

for (let i = 0; i < eventCount; i++) {
  const title = titles[i];
  const date = dates[i];

  // 1. Skip if the title is empty or we've already processed this event
  if (!title || seenTitles.has(title)) {
    continue;
  }

  // 2. Filter out irrelevant "Trends" items that start with a number (e.g., "1. Tickets")
  if (/^\d+\.\s/.test(title)) {
    continue;
  }

  // 3. Add the unique event to our results (without the URL)
  cleanedEvents.push({
    "Title": title,
    "Date and Time": date
  });

  // 4. Mark this title as seen to avoid adding it again
  seenTitles.add(title);
}

// Return the newly structured and cleaned array of event objects
return cleanedEvents;
