const titles = items[0].json.Title;
const prices = items[0].json.Price;

const output = [];

for (let i = 0; i < titles.length; i++) {
  const title = titles[i];
  const priceStr = prices[i];
  const price = parseFloat(priceStr.replace(/[^0-9.]/g, ''));

  output.push({
    json: {
      title,
      price
    }
  });
}

return output;
