// Get the HTML content from the previous node.
// Assuming your HTTP Request node's output is named 'data'.
const html = items[0].json.data;

// This regex finds the specific <script> tag containing the search results.
const regex = /<script id="data-deferred-state-0"[^>]*>([\s\S]*?)<\/script>/;
const match = html.match(regex);

if (!match || !match[1]) {
  console.log("Could not find the data script tag.");
  return [{
    error: "Data script tag not found. The page structure might have changed."
  }];
}

try {
  // Parse the JSON string found inside the script tag.
  const jsonData = JSON.parse(match[1]);

  // Navigate through the complex object to find the search results array.
  // Using optional chaining (?.) prevents errors if a key doesn't exist.
  const searchResults = jsonData?.niobeMinimalClientData?.[0]?.[1]?.data?.presentation?.staysSearch?.results?.searchResults;

  if (!searchResults || !Array.isArray(searchResults)) {
    console.log("Could not find search results in the JSON data.");
    return [{
      error: "Search results not found in the JSON data. The data structure may have changed."
    }];
  }

  // Map over the results to extract the title and price for each listing.
  const listings = searchResults.map(item => {
    const title = item.title;

    // The price can be in 'discountedPrice' or 'price', so we check for both.
    const primaryLine = item.structuredDisplayPrice?.primaryLine;
    let price = "N/A";
    if (primaryLine) {
      const priceValue = primaryLine.discountedPrice || primaryLine.price;
      const qualifier = primaryLine.qualifier || '';
      price = `${priceValue} ${qualifier}`.trim();
    }

    return {
      title,
      price,
    };
  });

  // n8n expects an array of objects to be returned.
  // Each object will become a separate item for the next node.
  return listings;

} catch (e) {
  console.error("Error parsing JSON data:", e);
  return [{
    error: "Failed to parse JSON data from the script tag."
  }];
}
