// Extract content and prepare Rally API payload
let rssData = {};
let httpData = {};

try {
  // Get RSS data from the dedup + URL extraction step
  rssData = $('Dedup + url extraction').item.json || {};
} catch (e) {
  console.log('No RSS data accessible:', e.message);
}

try {
  // Get HTTP data from the content fetching step
  httpData = $('Get Content').item.json || {};
} catch (e) {
  console.log('No HTTP data accessible:', e.message);
}

// Extract HTML content
const htmlContent = httpData.data || httpData.body || '';

// Extract RSS metadata
const title = rssData.title || '';
const snippet = rssData.contentSnippet || rssData.content || rssData.summary || '';
const articleUrl = rssData.cleanUrl || rssData.link || '';

// Extract article content from HTML
let extractedContent = '';
if (htmlContent) {
  extractedContent = htmlContent
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]*>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/\s+/g, ' ')
    .trim();

  // Limit length
  if (extractedContent.length > 3000) {
    extractedContent = extractedContent.substring(0, 3000) + '...';
  }
}

// Use extracted content if substantial, otherwise RSS snippet
const useExtracted = extractedContent.length > 200;
const fullContent = useExtracted ? extractedContent : snippet;
const memoryContent = [`You've just read this content: ${fullContent}`];

// Rally API payload
const rallyPayload = {
  smart: false,
  provider: "openai",
  query: "After reading that content, how has your interest in spending money on synthetic research changed? A) Much more interested, B) Somewhat more interested, C) No change, D) Somewhat less interested, E) Much less interested",
  audience_id: "r8eb276513d8241",
  voting_mode: true,
  mode: "fast",
  manual_memories: memoryContent,
};

return {
  title: title,
  content: snippet,
  url: articleUrl,
  fullContent: fullContent,
  rallyPayload: rallyPayload,
  extractedLength: extractedContent.length,
  usedExtracted: useExtracted,
  debug: {
    hasTitle: !!title,
    hasUrl: !!articleUrl,
    htmlLength: htmlContent.length,
    rssDataFound: Object.keys(rssData).length > 0,
    httpDataFound: Object.keys(httpData).length > 0
  }
};

