parameters.jsCode:
// Process each Rally API response individually
const item = $input.item.json;

let voteCounts = { A: 0, B: 0, C: 0, D: 0, E: 0 };
let totalVoters = 0;
let responses = [];

try {
  // The Rally API response is in 'responses' array
  if (item.responses && Array.isArray(item.responses)) {
    totalVoters = item.responses.length;

    for (const personaResponse of item.responses) {
      try {
        // Parse each persona's response
        const data = JSON.parse(personaResponse.response);
        const option = data.option;

        if (option && voteCounts.hasOwnProperty(option)) {
          voteCounts[option]++;
        }

        responses.push({
          persona_id: personaResponse.persona_id,
          option: option,
          thinking: data.thinking || '',
          thoughts: personaResponse.thoughts
        });

      } catch (parseErr) {
        console.log(`Error parsing persona ${personaResponse.persona_id} response:`, parseErr.message);
      }
    }
  }
} catch (err) {
  console.log('Error processing Rally response:', err.message);
}

// Calculate percentages for this individual simulation
const percentages = {};
for (const opt of Object.keys(voteCounts)) {
  percentages[opt] = totalVoters > 0
    ? Math.round((voteCounts[opt] / totalVoters) * 100)
    : 0;
}

const proNarrative = percentages.A + percentages.B;
const contraNarrative = percentages.D + percentages.E;
const neutral = percentages.C;

// Determine narrative type and select sample responses
let notificationType = '';
let sampleResponses = [];

if (proNarrative >= 75) {
  notificationType = 'pro-narrative';
  // Get 5 random responses from A & B voters
  const proResponses = responses.filter(r => ['A', 'B'].includes(r.option));
  sampleResponses = proResponses.sort(() => 0.5 - Math.random()).slice(0, 5);
} else if (contraNarrative >= 75) {
  notificationType = 'contra-narrative';
  // Get 5 random responses from D & E voters
  const contraResponses = responses.filter(r => ['D', 'E'].includes(r.option));
  sampleResponses = contraResponses.sort(() => 0.5 - Math.random()).slice(0, 5);
} else {
  notificationType = 'mixed';
  // Get 5 random responses from all voters
  sampleResponses = responses.sort(() => 0.5 - Math.random()).slice(0, 5);
}

// Return individual result for this RSS item
return {
  // Rally simulation metadata
  session_id: item.session_id || '',
  title: item.title || 'Interest in Synthetic Research Spending',

  // Rally simulation results
  totalVoters,
  voteCounts,
  percentages,
  proNarrative,
  contraNarrative,
  neutral,
  responses,
  summary: item.summary || '',

  // Pre-selected sample responses for email
  sampleResponses,
  notificationType,

  // Metadata
  simulationId: item.session_id || `sim_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,

  // Debug info
  debug: {
    foundResponses: !!item.responses,
    responseCount: item.responses ? item.responses.length : 0,
    hasSessionId: !!item.session_id,
    sampleResponsesCount: sampleResponses.length
  }
};

// Process each Rally API response individually
const item = $input.item.json;

// The data is already processed - just extract what we need
const voteCounts = item.voteCounts || { A: 0, B: 0, C: 0, D: 0, E: 0 };
const totalVoters = item.totalVoters || 0;
const responses = item.responses || [];

// Calculate percentages
const percentages = {};
for (const opt of Object.keys(voteCounts)) {
  percentages[opt] = totalVoters > 0
    ? Math.round((voteCounts[opt] / totalVoters) * 100)
    : 0;
}

// Calculate narrative aggregates
const proNarrative = percentages.A + percentages.B;
const contraNarrative = percentages.D + percentages.E;
const neutral = percentages.C;

// Determine notification type
let notificationType, message;
if (proNarrative >= 75) {
  notificationType = 'pro-narrative';
  message = `ðŸŸ¢ Pro-Narrative Detected: ${proNarrative}% voted A+B ` +
            `(${percentages.A}% A, ${percentages.B}% B)`;
} else if (contraNarrative >= 75) {
  notificationType = 'contra-narrative';
  message = `ðŸ”´ Contra-Narrative Detected: ${contraNarrative}% voted D+E ` +
            `(${percentages.D}% D, ${percentages.E}% E)`;
} else {
  notificationType = 'mixed';
  message = `âšª Mixed Response: Pro=${proNarrative}%, ` +
            `Contra=${contraNarrative}%, Neutral=${neutral}%`;
}

// Select sample responses based on narrative type
let sampleResponses = [];
if (responses.length > 0) {
  let targetOptions = [];

  if (notificationType === 'pro-narrative') {
    targetOptions = ['A', 'B'];
  } else if (notificationType === 'contra-narrative') {
    targetOptions = ['D', 'E'];
  } else {
    // For mixed, get a variety
    targetOptions = ['A', 'B', 'C', 'D', 'E'];
  }

  // Filter responses by target options and take up to 5
  sampleResponses = responses
    .filter(r => targetOptions.includes(r.option))
    .slice(0, 5);
}

// Return the processed data
return {
  session_id: item.session_id,
  title: item.title,
  totalVoters,
  voteCounts,
  percentages,
  proNarrative,
  contraNarrative,
  neutral,
  responses,
  summary: item.summary,
  sampleResponses,
  notificationType,
  message,
  simulationId: item.session_id,
  shouldNotify: proNarrative >= 75 || contraNarrative >= 75,
  debug: {
    foundResponses: responses.length > 0,
    responseCount: responses.length,
    hasSessionId: !!item.session_id,
    sampleResponsesCount: sampleResponses.length
  }
};

// Multi-item deduplication + URL extraction
const items = $input.all();
const hoursThreshold = 3; // Adjust as needed (24 hours = 1 day)
const now = new Date();

console.log(`Processing ${items.length} RSS items for deduplication and URL extraction`);

const processedItems = items.filter((item, index) => {
  try {
    // First: Check age for deduplication
    const pubDate = new Date(item.json.pubDate || item.json.isoDate);
    const ageInHours = (now - pubDate) / (1000 * 60 * 60);

    console.log(`Item ${index}: "${item.json.title}" - Age: ${ageInHours.toFixed(1)} hours`);

    if (ageInHours <= hoursThreshold) {
      console.log(`âœ… Item ${index} passed filter (within ${hoursThreshold} hours)`);
      return true;
    } else {
      console.log(`âŒ Item ${index} filtered out (older than ${hoursThreshold} hours)`);
      return false;
    }
  } catch (error) {
    console.log(`âš ï¸ Error processing item ${index}:`, error.message);
    return false;
  }
}).map(item => {
  // Second: Extract clean URL for items that passed the filter
  const originalUrl = item.json.link;
  let cleanUrl = originalUrl;

  // Handle Google redirect URLs (from Google Alerts)
  if (originalUrl && originalUrl.includes('google.com/url')) {
    const match = originalUrl.match(/[&?]url=([^&]+)/);
    if (match) {
      try {
        cleanUrl = decodeURIComponent(match[1]);
        console.log(`ðŸ”— URL extracted: ${originalUrl} â†’ ${cleanUrl}`);
      } catch (error) {
        console.log('URL decode error:', error);
        cleanUrl = originalUrl;
      }
    }
  }

  // Return item with clean URL added
  return {
    json: {
      ...item.json,
      cleanUrl: cleanUrl
    }
  };
});

console.log(`Final result: ${processedItems.length} items processed with clean URLs`);

return processedItems;

parameters.url:
={{ 
  (() => {
    const url = $json.link;
    if (url.includes('google.com/url')) {
      const match = url.match(/[&?]url=([^&]+)/);
      return match ? decodeURIComponent(match[1]) : url;
    }
    return url;
  })() 
}}
