options.customCss:
/* --- ROOT VARIABLES (DARK THEME) --- */
:root {
	/* --- FONT MAPPING --- */
	--font-family: 'Inter', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* --- COLOR MAPPING (Dark Theme) --- */
	--color-background: #1a1b26;
	--color-card-bg: #24283b;
	--color-input-bg: #1e202e;
	--color-card-border: #414868;
	--color-header: #ffffff;
	--color-label: #c0caf5;
	--color-input-text: #c0caf5;
	--color-header-subtext: #c0caf5;
	--color-html-text: #c0caf5;
	--color-input-border: #414868;
	--color-focus-border: #7aa2f7;
	--color-link: #9eceff;
	--color-html-link: #7aa2f7;
	--color-submit-btn-bg: #7aa2f7;
	--color-submit-btn-hover-bg: #9eceff;
	--color-submit-btn-text: #ffffff; /* CORRECTED: Changed back to white for better readability */
	--color-clear-button-bg: #414868;
	--color-error: #f7768e;
	--color-required: #f7768e;
	--color-test-notice-text: #c0caf5;
	--color-test-notice-bg: #2a2f41;
	--color-test-notice-border: #7aa2f7;

	/* --- BORDER RADII & SPACING --- */
	--border-radius-card: 12px;
	--border-radius-input: 6px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 12px;
	--padding-container-top: 24px;
	--padding-card: 24px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 16px;
	--padding-form-input: 12px;
	--card-padding: 24px;
	--card-margin-bottom: 16px;

	/* --- DIMENSIONS --- */
	--container-width: 500px;
	--submit-btn-height: 48px;
	--checkbox-size: 18px;

	/* --- OTHER MAPPINGS --- */
	--box-shadow-card: 0 10px 30px rgba(0, 0, 0, 0.3);
	--color-card-shadow: rgba(0, 0, 0, 0.3);
	--opacity-placeholder: 0.5;
}

/* === GLOBAL & ELEMENT-SPECIFIC FIXES === */

/* 1. Add the dot grid background to the page */
body {
    background-image: radial-gradient(circle at 1px 1px, rgba(255,255,255,0.05) 1px, transparent 0);
    background-size: 20px 20px;
}

/* 2. Explicitly style the TEXTAREA */
/* Targets the specific class n8n uses */
textarea.form-input {
    background-color: var(--color-input-bg);
    color: var(--color-input-text);
    font-family: var(--font-family); /* Force font inheritance */
}
textarea.form-input::placeholder {
    color: var(--color-input-text);
    opacity: var(--opacity-placeholder);
}

/* 3. Explicitly style the BUTTON */
button.submit-btn {
    transition: background-color 0.2s ease;
    font-family: var(--font-family); /* Force font inheritance */
    font-weight: var(--font-weight-bold);
    color: var(--color-submit-btn-text); /* Ensure text color is applied */
}
button.submit-btn:hover {
    background-color: var(--color-submit-btn-hover-bg);
}

/* 4. Completely new CHECKBOX styling for n8n's structure */
/* Target the label that wraps the checkbox and text */
label.multiselect-label {
    display: flex;
    align-items: center;
    cursor: pointer;
    position: relative;
}
/* Target the text part of the label */
label.multiselect-label span {
    padding-left: 12px; /* Space between box and text */
}
/* Hide the default browser checkbox */
label.multiselect-label input[type="checkbox"] {
    appearance: none;
    -webkit-appearance: none;
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
}
/* Create a custom checkbox box using a pseudo-element on the text span */
label.multiselect-label span::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: var(--checkbox-size);
    height: var(--checkbox-size);
    background-color: var(--color-input-bg);
    border: 1px solid var(--color-input-border);
    border-radius: 4px;
    transition: all 0.2s ease;
}
/* Style the custom box when the (hidden) checkbox is checked */
label.multiselect-label input[type="checkbox"]:checked + span::before {
    background-color: var(--color-focus-border);
    border-color: var(--color-focus-border);
    /* Add a checkmark using an embedded SVG */
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16' fill='white'%3e%3cpath d='M12.207 4.793a1 1 0 010 1.414l-5 5a1 1 0 01-1.414 0l-2-2a1 1 0 011.414-1.414L6.5 9.086l4.293-4.293a1 1 0 011.414 0z'/%3e%3c/svg%3e");
    background-position: center;
    background-repeat: no-repeat;
}
/* Add a focus ring for accessibility */
label.multiselect-label input[type="checkbox"]:focus-visible + span::before {
    outline: 2px solid var(--color-focus-border);
    outline-offset: 2px;
}

parameters.responseBody:
=<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Prompt Viewer</title>

    <!-- Fonts from Google Fonts for a professional look -->
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;700&family=Fira+Code:wght@400;500&display=swap" rel="stylesheet">

    <style>
        /* --- Reset and Base Styles --- */
        :root {
            --bg-color: #1a1b26;
            --card-bg: #24283b;
            --code-bg: #1e202e; /* Slightly different bg for the code block */
            --text-color: #c0caf5;
            --header-color: #ffffff;
            --accent-color: #7aa2f7;
            --accent-hover: #9eceff;
            --success-color: #9ece6a;
            --border-color: #414868;
            --shadow-color: rgba(0, 0, 0, 0.3);
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Inter', sans-serif;
            background-color: var(--bg-color);
            color: var(--text-color);
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            padding: 2rem;
            background-image: radial-gradient(circle at 1px 1px, rgba(255,255,255,0.05) 1px, transparent 0);
            background-size: 20px 20px;
        }

        /* --- Main Container --- */
        .container {
            width: 100%;
            max-width: 900px;
            background-color: var(--card-bg);
            border-radius: 12px;
            border: 1px solid var(--border-color);
            box-shadow: 0 10px 30px var(--shadow-color);
            overflow: hidden;
            display: flex;
            flex-direction: column;
        }

        /* --- Card Header --- */
        .header {
            padding: 1.5rem 2rem;
            border-bottom: 1px solid var(--border-color);
        }

        .header h1 {
            font-size: 1.8rem;
            font-weight: 700;
            color: var(--header-color);
            margin-bottom: 0.25rem;
        }

        .header p {
            font-size: 1rem;
            color: var(--text-color);
        }

        /* --- Prompt Content --- */
        .prompt-content {
            position: relative;
            padding: 2rem;
            padding-top: 4.5rem; /* Space for the copy button */
        }

        /* --- Markdown-style Code Block --- */
        pre {
            background-color: var(--code-bg);
            border: 1px solid var(--border-color);
            border-radius: 8px;
            padding: 1.5rem;
            font-family: 'Fira Code', monospace;
            font-size: 0.9rem;
            line-height: 1.6;
            color: var(--text-color);

            /* KEY FOR TEXT WRAPPING */
            white-space: pre-wrap;
            overflow-wrap: break-word;
        }

        /* --- Copy Button --- */
        .copy-button {
            position: absolute;
            top: 1.5rem;
            right: 1.5rem;
            background-color: var(--border-color);
            color: var(--text-color);
            border: none;
            border-radius: 6px;
            padding: 0.6rem 1rem;
            font-family: 'Inter', sans-serif;
            font-weight: 500;
            font-size: 0.9rem;
            cursor: pointer;
            display: flex;
            align-items: center;
            gap: 0.5rem;
            transition: background-color 0.2s ease, color 0.2s ease;
            z-index: 10; /* Ensure it's above the <pre> block */
        }

        .copy-button:hover {
            background-color: var(--accent-color);
            color: var(--header-color);
        }

        .copy-button.copied {
            background-color: var(--success-color);
            color: var(--bg-color);
        }

        .copy-button svg {
            width: 16px;
            height: 16px;
            stroke-width: 2;
        }

        /* --- Footer --- */
        .footer {
            text-align: center;
            padding: 1rem;
            font-size: 0.8rem;
            color: var(--border-color);
            border-top: 1px solid var(--border-color);
        }

        .footer a {
            color: var(--accent-color);
            text-decoration: none;
            transition: color 0.2s ease;
        }

        .footer a:hover {
            color: var(--accent-hover);
        }
    </style>
</head>
<body>

    <div class="container">
        <header class="header">
            <h1>Your Custom Prompt</h1>
            <p>Use the button below to copy the entire prompt to your clipboard.</p>
        </header>

        <main class="prompt-content">
            <button id="copy-btn" class="copy-button">
                <svg id="copy-icon" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect><path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"></path></svg>
                <svg id="check-icon" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" style="display: none;"><polyline points="20 6 9 17 4 12"></polyline></svg>
                <span id="copy-text">Copy</span>
            </button>

            <!-- 
                This block is now a simple placeholder.
                We will populate it using JavaScript to ensure the prompt text,
                including all its tags, is displayed literally.
            -->
            <pre><code id="prompt-display-code"></code></pre>
        </main>

        <footer class="footer">
            <p>Generated with ‚ù§Ô∏è by <a href="https://n8n.partnerlinks.io/g3yh568uv03t" target="_blank" rel="noopener noreferrer">n8n</a></p>
        </footer>
    </div>

    <!-- 
        This is the most important part for perfect copy/display.
        We embed the raw prompt data into a JavaScript variable.
        JSON.stringify() escapes any special characters, making it a valid JS string.
    -->
    <script>
        const rawPromptToCopy = {{ JSON.stringify($json.query.prompt) }};
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- Part 1: Display the prompt safely ---
            const promptDisplayElement = document.getElementById('prompt-display-code');
            if (promptDisplayElement) {
                // Using .textContent is the key. It tells the browser to treat
                // the string as plain text, not as HTML. This automatically
                // displays '<' and '>' characters correctly.
                promptDisplayElement.textContent = rawPromptToCopy;
            }

            // --- Part 2: Handle the copy functionality ---
            const copyButton = document.getElementById('copy-btn');
            const copyText = document.getElementById('copy-text');
            const copyIcon = document.getElementById('copy-icon');
            const checkIcon = document.getElementById('check-icon');

            copyButton.addEventListener('click', () => {
                // The clipboard API copies the raw, unmodified string.
                navigator.clipboard.writeText(rawPromptToCopy)
                    .then(() => {
                        // Success feedback
                        copyText.textContent = 'Copied!';
                        copyButton.classList.add('copied');
                        copyIcon.style.display = 'none';
                        checkIcon.style.display = 'inline-block';

                        // Reset the button after 2 seconds
                        setTimeout(() => {
                            copyText.textContent = 'Copy';
                            copyButton.classList.remove('copied');
                            copyIcon.style.display = 'inline-block';
                            checkIcon.style.display = 'none';
                        }, 2000);
                    })
                    .catch(err => {
                        console.error('Failed to copy: ', err);
                        copyText.textContent = 'Error';
                        setTimeout(() => { copyText.textContent = 'Copy'; }, 2000);
                    });
            });
        });
    </script>

</body>
</html>
