// The input is an array of items. We'll work with the first item.


// Access the string value from the 'output' field in the input JSON.
const rawStringWithMarkdown = item.json.output;
console.log(item.json.output)
// The JSON we want is nested inside a markdown code block (```json ... ```).
// We need to extract just the JSON part.

// Find the first occurrence of '{' to locate the start of the JSON object.
const jsonStartIndex = rawStringWithMarkdown.indexOf('{');

// Find the last occurrence of '}' to locate the end of the JSON object.
const jsonEndIndex = rawStringWithMarkdown.lastIndexOf('}');

// Slice the string from the start to the end to get only the clean JSON string.
// We add +1 to jsonEndIndex because substring's second argument is exclusive.
const cleanJsonString = rawStringWithMarkdown.substring(jsonStartIndex, jsonEndIndex + 1);

// Parse the cleaned string into a proper, usable JSON object.
const parsedJson = JSON.parse(cleanJsonString);

// Return the parsed JSON object. The keys (symbol, sentiment_score, etc.)
// will become individual fields in the n8n output for the next node to use.
return parsedJson;}
