// 1. extract raw records
const records = $input.all().map(item => item.json);

// 2. date utilities
function parseYMD(s){ return new Date(s + 'T00:00:00'); }
function formatYMD(d){
  const y = d.getFullYear();
  const m = String(d.getMonth()+1).padStart(2,'0');
  const da = String(d.getDate()).padStart(2,'0');
  return `${y}-${m}-${da}`;
}
function rangeDates(start, end){
  const a = [];
  let cur = new Date(start);
  while(cur <= end){
    a.push(formatYMD(cur));
    cur.setDate(cur.getDate()+1);
  }
  return a;
}

// 3. find global start/end
const dates = records.flatMap(r=>[r.date_start, r.date_stop]);
const minDate = new Date(Math.min(...dates.map(d=>parseYMD(d))));
const maxDate = new Date(Math.max(...dates.map(d=>parseYMD(d))));
const allLabels = rangeDates(minDate, maxDate);

// 4. overview totals
const overviewAgg = records.reduce((acc, r) => {
  const imp = Number(r.impressions || 0);
  const clk = Number(r.inline_link_clicks || 0);
  const spd = Number(r.spend || 0);
  acc.impressions += imp;
  acc.clicks     += clk;
  acc.investment += spd;
  return acc;
}, { impressions:0, clicks:0, investment:0 });

const overview = {
  investment:  { value: Number(overviewAgg.investment.toFixed(2)) },
  impressions: { value: overviewAgg.impressions },
  clicks:      { value: overviewAgg.clicks },
  ctr:         { 
    value: overviewAgg.impressions
      ? `${(overviewAgg.clicks / overviewAgg.impressions * 100).toFixed(2)}%`
      : '0.00%' 
  },
  cpc:         { 
    value: overviewAgg.clicks
      ? Number((overviewAgg.investment / overviewAgg.clicks).toFixed(4))
      : 0 
  }
};

// 5. day_by_day
const dayMap = records.reduce((m, r) => {
  const d = r.date_start;
  if (!m[d]) m[d] = { impressions:0, clicks:0 };
  m[d].impressions += Number(r.impressions || 0);
  m[d].clicks     += Number(r.inline_link_clicks || 0);
  return m;
}, {});
const day_by_day = {
  labels:      allLabels,
  impressions: allLabels.map(d => dayMap[d]?.impressions || 0),
  clicks:      allLabels.map(d => dayMap[d]?.clicks      || 0),
};

// 6. helper to aggregate by key (com formata√ß√£o de investimento em 2 casas)
function aggBy(records, keyField){
  const map = {};
  records.forEach(r => {
    const key = r[keyField] || 'N/A';
    if (!map[key]) map[key] = { 
      name: key, impressions:0, clicks:0, investment:0 
    };
    map[key].impressions += Number(r.impressions || 0);
    map[key].clicks      += Number(r.inline_link_clicks || 0);
    map[key].investment  += Number(r.spend || 0);
  });
  return Object.values(map).map(o => ({
    name:        o.name,
    impressions: o.impressions,
    clicks:      o.clicks,
    investment:  Number(o.investment.toFixed(2)),               // <-- 2 decimais aqui
    ctr:         o.impressions
                   ? `${(o.clicks / o.impressions * 100).toFixed(2)}%`
                   : '0.00%',
    cpc:         o.clicks
                   ? Number((o.investment / o.clicks).toFixed(4))
                   : 0,
  }));
}

// 7. campaigns, ad-sets, ads
const byCampaign = aggBy(records, "campaign_name");
const byAdSet    = aggBy(records, "adset_name");
const byAd       = aggBy(records, "ad_name");

// 8. top 10 cheapest CPC
const top10Campaigns = [...byCampaign]
  .sort((a,b)=>a.cpc - b.cpc)
  .slice(0,10)
  .map(c=>({
    campaign_name: c.name,
    investment:    c.investment,
    impressions:   c.impressions,
    ctr:           c.ctr,
    clicks:        c.clicks,
    cpc:           c.cpc
  }));
const top10AdSets = [...byAdSet]
  .sort((a,b)=>a.cpc - b.cpc)
  .slice(0,10)
  .map(a=>({
    ad_group_name: a.name,
    investment:    a.investment,
    impressions:   a.impressions,
    ctr:           a.ctr,
    clicks:        a.clicks,
    cpc:           a.cpc
  }));
const top10Ads = [...byAd]
  .sort((a,b)=>a.cpc - b.cpc)
  .slice(0,10)
  .map(a=>({
    ad_group_name: a.name,
    investment:    a.investment,
    impressions:   a.impressions,
    ctr:           a.ctr,
    clicks:        a.clicks,
    cpc:           a.cpc
  }));

// 9. comparisons ordered by investment desc
const adsComparison    = byAd.sort((a,b)=>b.investment - a.investment);
const adSetsComparison = byAdSet.sort((a,b)=>b.investment - a.investment);

// 10. monta o relat√≥rio final
const report = {
  report_date: `${formatYMD(minDate)} - ${formatYMD(maxDate)}`,
  overview,
  day_by_day,
  top_10_campaigns:   top10Campaigns,
  ad_set_comparison: {
    labels:      adSetsComparison.map(a=>a.name),
    investment:  adSetsComparison.map(a=>Number(a.investment.toFixed(2))),
    impressions: adSetsComparison.map(a=>a.impressions),
    clicks:      adSetsComparison.map(a=>a.clicks),
  },
  top_10_ad_sets:     top10AdSets,
  ads_comparison: {
    labels:      adsComparison.map(a=>a.name),
    investment:  adsComparison.map(a=>Number(a.investment.toFixed(2))),
    impressions: adsComparison.map(a=>a.impressions),
    clicks:      adsComparison.map(a=>a.clicks),
  },
  top_10_ads:        top10Ads
};

// 11. retorna um √∫nico item com o JSON pronto
return [{ json: report }];

const base = $("Format data").first().json

return {
      ...base,                                           
      insights_content:     $json.output.campaign_insights,
      insights_ad_set_content:$json.output.adset_insights,
      insights_ads_content:  $json.output.ad_insights,
    };
