const axios = require('axios');

const OPENAI_API_KEY = '<openai_api_token>';
const SUPABASE_URL = 'https://<supabase_id>.supabase.co';
const SUPABASE_API_KEY = '<supabase_api_token>';


// Get input from the previous node
const queryText = query.query;
const filters = $('Aggregate files').item.json || {}; // Should include { file_id: ["uuid1", "uuid2"] }

async function getEmbedding(text) {
    if (!text || typeof text !== 'string') {
        throw new Error(`Invalid input for embedding: "${text}"`);
    }

    try {
        console.log("üîπ Generating embedding for query:", text);
        const response = await axios.post(
            'https://api.openai.com/v1/embeddings',
            { input: text, model: 'text-embedding-3-small' },
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${OPENAI_API_KEY}`
                }
            }
        );

        console.log("‚úÖ OpenAI Embedding Generated Successfully!");
        return response.data.data[0].embedding;
    } catch (error) {
        console.error("‚ùå OpenAI Embedding Error:", error.response?.data || error.message);
        throw new Error(`OpenAI Embedding Error: ${JSON.stringify(error.response?.data || error.message)}`);
    }
}

async function fetchDocuments(queryEmbedding) {
    try {
        console.log("üîπ Sending request to Supabase...");
        console.log("üîπ Query Embedding Length:", queryEmbedding.length);
        console.log("üîπ Filters used:", JSON.stringify(filters, null, 2));

        const response = await axios.post(
            `${SUPABASE_URL}/rest/v1/rpc/match_documents`,
            {
                query_embedding: queryEmbedding,
                match_count: 5,
                filter: filters
            },
            {
                headers: {
                    'Content-Type': 'application/json',
                    'apikey': SUPABASE_API_KEY,
                    'Authorization': `Bearer ${SUPABASE_API_KEY}`
                }
            }
        );

        console.log("‚úÖ Supabase Response:", response.data);
        return response.data;
    } catch (error) {
        console.error("‚ùå Supabase Query Error:", error.response?.data || error.message);
        throw new Error(`Supabase Query Error: ${error.message}`);
    }
}

async function processQuery() {
    try {
        console.log("üîπ Processing Query:", queryText);
        console.log("üîπ Filters:", filters);
        console.log("üîé queryText:", JSON.stringify(queryText));

        const embedding = await getEmbedding(queryText);
        const results = await fetchDocuments(embedding);

        // Format results with file name and URL
        const formattedResults = results.map(item => ({
            id: item.id,
            content: item.content,
            similarity: item.similarity.toFixed(4),
            fileName: item.file_name,
            fileUrl: item.file_url
        }));

        // Combine content into a single string with file metadata
        const joinedContent = formattedResults.map(item => {
            return `üìÑ *${item.fileName || "Unnamed file"}* (${item.similarity} similarity)\nüîó ${item.fileUrl || "No link"}\n\n${item.content}`;
        }).join("\n\n---\n\n");

        console.log("‚úÖ Final Formatted Results:", formattedResults);
        console.log("‚úÖ Final Joined Content:", joinedContent);

        return joinedContent;
    } catch (error) {
        console.error("‚ùå Processing Error:", error.message);
        throw new Error(`Processing Error: ${error.message}`);
    }
}

return processQuery();

