// Parse incoming Telegram data
const message = items[0].json.message;
const user = message.from;
const text = message.text || '';
const chatId = message.chat.id;

// Determine message type
let messageType = 'text';
let command = null;
let isCommand = false;

if (text.startsWith('/')) {
  isCommand = true;
  command = text.split(' ')[0].toLowerCase();
  messageType = 'command';
}

// Extract user info
const userData = {
  telegram_id: user.id,
  username: user.username || '',
  first_name: user.first_name || '',
  last_name: user.last_name || '',
  chat_id: chatId
};

return [{
  json: {
    message_type: messageType,
    command: command,
    text: text,
    user: userData,
    raw_message: message,
    is_command: isCommand
  }
}];

// Merge user data from either existing user or newly created user
const telegramData = $('Parse Telegram Data').first().json;
const existingUser = $('Check Existing User').first().json;
const newUser = $input.all().find(item => item.json.id); // From Create New User
console.log(existingUser, 'ovo')
let userData;
if (existingUser) {
  userData = existingUser;
} else if (newUser) {
  userData = newUser.json;
} else {
  userData = {
    telegram_id: telegramData.user.telegram_id,
    score: 0,
    game_state: 'idle'
  };
}

return [{
  json: {
    ...telegramData,
    user_data: userData
  }
}];

const command = items[0].json.command;
const userData = items[0].json.user_data;
const userName = userData.first_name || userData.username || 'Player';

let responseText = '';

switch(command) {
  case '/start':
    responseText = `üéâ Welcome to Trivia Bot, ${userName}!

üß† Test your knowledge with our trivia questions!

Commands:
/question - Get a new question
/score - Check your score
/leaderboard - View top players
/stats - Your statistics
/help - Show this help

Good luck! üçÄ`;
    break;

  case '/help':
    responseText = `üìö **Trivia Bot Help**

üéØ **Commands:**
/question - Get a new trivia question
/score - Check your current score
/leaderboard - View top 10 players
/stats - Your detailed statistics
/help - Show this help message

üéÆ **How to Play:**
1. Use /question to get a new question
2. Reply with A, B, C, or D
3. Earn points for correct answers
4. Climb the leaderboard!

üèÜ **Scoring:**
- ‚≠ê: 1 point
- ‚≠ê‚≠ê: 2 points
- ‚≠ê‚≠ê‚≠ê: 3 points
- ‚≠ê‚≠ê‚≠ê‚≠ê: 4 points
- ‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê: 5 points

Have fun! üéâ`;
    break;

  case '/score':
    responseText = `üèÜ **Your Score**

üìä Current Score: ${userData.score || 0} points
üéÆ Games Played: ${userData.games_played || 0}
‚úÖ Correct Answers: ${userData.correct_answers || 0}

Keep playing to improve your score! üí™`;
    break;

  case '/stats':
    const accuracy = userData.games_played > 0 ? 
      Math.round((userData.correct_answers / userData.games_played) * 100) : 0;
    responseText = `üìà **Your Statistics**

üèÜ Total Score: ${userData.score || 0} points
üéØ Accuracy: ${accuracy}%
üéÆ Games Played: ${userData.games_played || 0}
‚úÖ Correct Answers: ${userData.correct_answers || 0}
‚ùå Wrong Answers: ${(userData.games_played || 0) - (userData.correct_answers || 0)}

You're doing great! Keep it up! üöÄ`;
    break;

  default:
    responseText = `‚ùì Unknown command: ${command}

Use /help to see available commands.`;
}

return [{
  json: {
    chat_id: items[0].json.user.chat_id,
    response_text: responseText,
    command: command
  }
}];

const question = $('Get Random Question').first().json;
const userData = $('Merge User Data').first().json.user;

if (!question) {
  return [{
    json: {
      chat_id: userData.chat_id,
      response_text: "‚ùå Sorry, no questions available right now. Please try again later."
    }
  }];
}

const questionText = `üß† **Trivia Question** (${question.difficulty || 'Medium'})

‚ùì ${question.question}

A) ${question.option_a}
B) ${question.option_b}
C) ${question.option_c}
D) ${question.option_d}

üí° Reply with A, B, C, or D!`;

return [{
  json: {
    chat_id: userData.chat_id,
    response_text: questionText,
    question_data: question,
    user_data: userData
  }
}];

const leaderboard = $input.first().json.leaderboard;
const currentUser = $('Merge User Data').item.json.user_data;

if (!leaderboard || leaderboard.length === 0) {
  return [{
    json: {
      chat_id: $('Merge User Data').item.json.user.chat_id,
      response_text: "üèÜ **Leaderboard**\n\nNo players yet! Be the first to play!"
    }
  }];
}

let leaderboardText = "üèÜ **Trivia Leaderboard** üèÜ\n\n";

leaderboard.forEach((player, index) => {
  const emoji = index === 0 ? "ü•á" : index === 1 ? "ü•à" : index === 2 ? "ü•â" : "üî∏";
  const name = player.first_name || player.username || 'Anonymous';
  const isCurrentUser = player.telegram_id === currentUser.telegram_id;
  const marker = isCurrentUser ? " ‚Üê YOU" : "";

  leaderboardText += `${emoji} ${index + 1}. ${name}: ${player.score || 0} pts${marker}\n`;
});

// Find current user's rank if not in top 10
const currentUserRank = leaderboard.findIndex(p => p.telegram_id === currentUser.telegram_id);
if (currentUserRank === -1) {
  leaderboardText += `\nüîç Your rank: Not ranked yet`;
} else if (currentUserRank >= 10) {
  leaderboardText += `\nüîç Your rank: #${currentUserRank + 1}`;
}

leaderboardText += "\n\nüéÆ Keep playing to climb higher!";

return [{
  json: {
    chat_id: $('Merge User Data').item.json.user.chat_id,
    response_text: leaderboardText
  }
}];

const userAnswer = $('Merge User Data').first().json.text.trim().toUpperCase();
const question = $('Get Current Question').first().json;
const userData = $('Merge User Data').first().json.user_data;

if (!question) {
  return [{
    json: {
      chat_id: $('Merge User Data').first().json.user.chat_id,
      response_text: "‚ùå No active question found. Use /question to start a new one!"
    }
  }];
}

const correctAnswer = question.correct_answer.toUpperCase();
const isCorrect = userAnswer === correctAnswer;

// Calculate points based on difficulty
let points = question.difficulty;

const responseText = isCorrect ? 
  `‚úÖ **Correct!** üéâ

You earned ${points} point${points !== 1 ? 's' : ''}!

üí° ${question.explanation || 'Well done!'}

Use /question for another question!` :
  `‚ùå **Wrong!** üòî

The correct answer was: **${correctAnswer}**

üí° ${question.explanation || 'Better luck next time!'}

Use /question to try again!`;

return [{
  json: {
    chat_id: $('Merge User Data').first().json.user.chat_id,
    response_text: responseText,
    is_correct: isCorrect,
    points_earned: points,
    user_data: userData,
    question: question
  }
}];

// Handle unknown text when not waiting for answer
const userData = $input.first().json.user;
const text = $input.first().json.text;

let responseText = `ü§î I didn't understand "${text}"\n\n`;

if (userData.game_state === 'waiting_answer') {
  responseText += `You have an active question. Please answer with A, B, C, or D.`;
} else {
  responseText += `Available commands:
/question - Get a trivia question
/score - Check your score
/leaderboard - View rankings
/help - Show all commands`;
}

return [{
  json: {
    chat_id: $input.first().json.user.chat_id,
    response_text: responseText
  }
}];

// Define default categories if none exist in DB
const categories = [
  'Science & Nature',
  'History', 
  'Geography',
  'Sports',
  'Movies & TV',
  'Technology',
  'Food & Drink',
  'Art & Literature'
];

// Create requests for each category (5 questions per category)
const generationRequests = categories.map(category => ({
  category: category,
  questionsToGenerate: 5,
  timestamp: new Date().toISOString()
}));

return generationRequests.map(request => ({ json: request }));
