// Parse AI Agent output and prepare for routing
const aiOutput = $input.first().json.output;
let classification;

try {
  // Parse JSON string output from AI Agent
  classification = JSON.parse(aiOutput);
} catch (error) {
  // Fallback if parsing fails
  classification = {
    category: "Support",
    confidence: 0.5,
    reasoning: "AI response could not be parsed, defaulting to Support",
    suggestedResponse: ""
  };
}

// Map categories to Gmail Label IDs
// IMPORTANT: Update these Label IDs with your actual Gmail label IDs
const labelMapping = {
  "Inquiry": "Label_123456789",
  "Actionpoint": "Label_123456789", 
  "Support": "Label_123456789",
  "Newsletter": "Label_123456789"
};

// Combine with original email data and add label ID
return {
  json: {
    ...classification,
    labelId: labelMapping[classification.category] || labelMapping["Support"], // Fallback to Support
    emailId: $('Extract Email Data').first().json.emailId,
    subject: $('Extract Email Data').first().json.subject,
    sender: $('Extract Email Data').first().json.sender,
    content: $('Extract Email Data').first().json.content,
    receivedAt: $('Extract Email Data').first().json.receivedAt
  }
};
