const items = $input.all();

const updatedItems = items.map((item) => {
  const { name, email, businessName, message, timeline, budget } =
    item.json.body;
  return {
    name,
    email,
    businessName,
    "project intent/need": message,
    timeline,
    budget,
  };
});

return updatedItems;

