const crypto = require('crypto');
const fetch = require('node-fetch');

// Step 1: Fetch encrypted PulsePoint incident data
const url = "https://api.pulsepoint.org/v1/webapp?resource=incidents&agencyid=19100";
const response = await fetch(url);
const data = await response.json();

// Step 2: Decrypt response
const ct = Buffer.from(data.ct, "base64");
const iv = Buffer.from(data.iv, "hex");
const salt = Buffer.from(data.s, "hex");

const e = "CommonIncidents";
const password =
  e[13] +
  e[1] +
  e[2] +
  "brady" +
  "5" +
  "r" +
  e.toLowerCase()[6] +
  e[5] +
  "gs";

// Key derivation (Python-style)
let key = Buffer.alloc(0);
let block = null;
while (key.length < 32) {
  const hasher = crypto.createHash("md5");
  if (block) hasher.update(block);
  hasher.update(Buffer.from(password, "utf8"));
  hasher.update(salt);
  block = hasher.digest();
  key = Buffer.concat([key, block], key.length + block.length);
}
key = key.slice(0, 32);

// Decrypt
const decipher = crypto.createDecipheriv("aes-256-cbc", key, iv);
let decrypted = decipher.update(ct);
decrypted += decipher.final("utf8");

const cleanedOutput = decrypted
  .slice(1, decrypted.lastIndexOf('"'))
  .replace(/\\"/g, '"');
const finalData = JSON.parse(cleanedOutput);
const active = finalData.incidents.active;

// Step 3: Use static data to track seen incident IDs
const workflowStaticData = $getWorkflowStaticData('global');

if (!workflowStaticData.hasOwnProperty('seenIncidentIDs')) {
  workflowStaticData.seenIncidentIDs = [];
}

const seenIDs = workflowStaticData.seenIncidentIDs;
const newIncidents = active.filter(i => !seenIDs.includes(i.ID));
const newIDs = newIncidents.map(i => i.ID);

// Update static data and prune to last 100 entries
workflowStaticData.seenIncidentIDs = [...new Set([...seenIDs, ...newIDs])].slice(-100);

// Incident call type mapping
const incidentCodeMapping = {
  ME: "Medical Emergency",
  IFT: "Interfacility Transfer",
  CA: "Canceled Assignment",
  ER: "Emergency Response",
  CMA: "Community Medical Assistance",
  VEG: "Vegetation Fire",
  TC: "Traffic Collision",
  OI: "Other Incidents",
  AA: "Auto Aid",
  MU: "Mutual Aid",
  ST: "Strike Team",
  AE: "Aircraft Emergency",
  AES: "Aircraft Emergency Standby",
  AC: "Aircraft Crash",
  LZ: "Landing Zone",
  FULL: "Full Assignment",
  AF: "Appliance Fire",
  CHIM: "Chimney Fire",
  CB: "Controlled Burn / Prescribed Fire",
  ELF: "Electrical Fire",
  FIRE: "Fire",
  GAS: "Gas Main",
  HC: "Hazardous Condition",
  MCI: "Multi Casualty Incident",
  FLW: "Flood Warning",
  TOW: "Tornado Warning",
  TSW: "Tsunami Warning",
  EQ: "Earthquake",
  RL: "Residential Lockout",
  VL: "Vehicle Lockout",
  CL: "Commercial Lockout",
  SF: "Structural Fire",
  OD: "Overdose",
  HM: "Hazardous Materials Incident",
  MA: "Medical Alert",
  ES: "Emergency Services",
  AR: "Animal Rescue",
  ELR: "Elevator Rescue",
  USAR: "Urban Search and Rescue",
  VS: "Vessel Sinking",
  TCE: "Expanded Traffic Collision",
  TCT: "Traffic Collision Involving Train",
  RTE: "Railroad/Train Emergency",
  IF: "Illegal Fire",
  MF: "Marine Fire",
  OF: "Outside Fire",
  PF: "Pole Fire",
  GF: "Garbage Fire",
};

// Unit dispatch status mapping (example)
const statusMapping = {
  ER: "En Route",
  OS: "On Scene",
  AR: "At Hospital / Released",
  TR: "Transporting",
  TA: "Taking Assignment",
  CA: "Canceled",
  CL: "Cleared",
};

return newIncidents.map((incident) => {
  const callTypeCode = incident.PulsePointIncidentCallType;
  const callType = incidentCodeMapping[callTypeCode] || callTypeCode;

  const units = incident.Unit.map((unit) => ({
    UnitID: unit.UnitID,
    Status: statusMapping[unit.PulsePointDispatchStatus] || unit.PulsePointDispatchStatus,
    ClearedAt: unit.UnitClearedDateTime || null,
  }));

  return {
    json: {
      ID: incident.ID,
      Type: callType,
      Address: incident.FullDisplayAddress,
      Time: incident.CallReceivedDateTime,
      Location: {
        Lat: incident.Latitude,
        Lng: incident.Longitude,
      },
      Units: units,
    },
  };
});
