// Access the incoming item
const item = items[0];

// Create CombinedContent with 4 newlines between sections
const combinedContent = [
  item.json['Stylized Title Page'] || '',
  item.json["Table Of Contents"] || '',
  item.json.Introduction || '',
  item.json["Strengths Section"] || '',
  item.json["Weaknesses Section"] || '',
  item.json["Opportunities Section"] || '',
  item.json["Threats Section"] || '',
  item.json.Conclusion || ''
].join('\n\n\n\n');

// Return a new item with CombinedContent added, preserving everything else
return [{
  ...item,
  json: {
    ...item.json,
    CombinedContent: combinedContent
  }
}];

