<body>
  <div class="container">
    <div class="header">
      <h1>Simple N8N Redis CRUD App</h1>
    </div>
    <div class="content">
      <div class="form-container">
        <form id="item-form">
          <input type="hidden" id="item-id">
          <input type="text" id="item-name" placeholder="Enter item name..." required>
          <button type="submit">Add Item</button>
          <button type="button" id="cancel-edit" style="display:none;">Cancel</button>
        </form>
        <div style="margin-top: 1rem; text-align: center;">
          <button type="button" id="reset-btn" style="background: #dc3545; color: white;">Reset All Items</button>
        </div>
      </div>
      <div class="error" id="error"></div>
      <div class="table-container">
        <table>
          <thead>
            <tr><th>ID</th><th>Name</th><th>Actions</th></tr>
          </thead>
          <tbody id="items-tbody">
            <tr>
              <td colspan="3" class="empty-state">
                <p>No items found</p>
                <p>Add your first item using the form above</p>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
  <script>
    const API_URL = '{{ $json.API_URL }}';
    const form = document.getElementById('item-form');
    const itemIdInput = document.getElementById('item-id');
    const itemNameInput = document.getElementById('item-name');
    const cancelEditBtn = document.getElementById('cancel-edit');
    const errorDiv = document.getElementById('error');
    const tbody = document.getElementById('items-tbody');
    const resetBtn = document.getElementById('reset-btn');

    function showError(msg) {
      errorDiv.textContent = msg;
      setTimeout(() => { errorDiv.textContent = ''; }, 5000);
    }

    function renderItems(items) {
      tbody.innerHTML = '';
      if (items.length === 0 || !items[0].id) {
        tbody.innerHTML = `
          <tr>
            <td colspan="3" class="empty-state">
              <p>No items found</p>
              <p>Add your first item using the form above</p>
            </td>
          </tr>
        `;
        return;
      }

      items.forEach(item => {
        const tr = document.createElement('tr');
        tr.innerHTML = `
          <td><strong>#${item.id}</strong></td>
          <td>${item.name}</td>
          <td>
            <div class="action-buttons">
              <button class="btn-edit" onclick="editItem(${item.id}, '${item?.name?.replace(/'/g, "&#39;")}')">Edit</button>
              <button class="btn-delete" onclick="deleteItem(${item.id})">Delete</button>
            </div>
          </td>
        `;
        tbody.appendChild(tr);
      });
    }

    async function fetchItems() {
      try {
        const res = await fetch(API_URL);
        if (!res.ok) throw new Error('Failed to fetch items');
        const items = await res.json();
        renderItems(items);
      } catch (err) {
        showError(err.message);
      }
    }

    form.onsubmit = async (e) => {
      e.preventDefault();
      const id = itemIdInput.value;
      const name = itemNameInput.value.trim();
      if (!name) return;
      try {
        if (id) {
          // Update
          const res = await fetch(`${API_URL}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id, name })
          });
          if (!res.ok) throw new Error('Failed to update item');
        } else {
          // Create
          const res = await fetch(API_URL, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name })
          });
          if (!res.ok) throw new Error('Failed to add item');
        }
        form.reset();
        itemIdInput.value = '';
        form.querySelector('button[type="submit"]').textContent = 'Add Item';
        cancelEditBtn.style.display = 'none';
        await fetchItems();
      } catch (err) {
        showError(err.message);
      }
    };

    window.editItem = (id, name) => {
      itemIdInput.value = id;
      itemNameInput.value = name;
      form.querySelector('button[type="submit"]').textContent = 'Update Item';
      cancelEditBtn.style.display = '';
    };

    cancelEditBtn.onclick = () => {
      form.reset();
      itemIdInput.value = '';
      form.querySelector('button[type="submit"]').textContent = 'Add Item';
      cancelEditBtn.style.display = 'none';
    };

    window.deleteItem = async (id) => {
      if (!confirm('Delete this item?')) return;
      try {
        const res = await fetch(`${API_URL}`, { 
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ id })
        });
        if (!res.ok) throw new Error('Failed to delete item');
        await fetchItems();
      } catch (err) {
        showError(err.message);
      }
    };

    // Initial load
    fetchItems();

    // Reset functionality
    async function resetItems() {
      if (!confirm('Are you sure you want to reset all items? This action cannot be undone.')) return;
      try {
        const res = await fetch(`${API_URL}-reset`, { method: 'GET' });
        if (!res.ok) throw new Error('Failed to reset items');
        await fetchItems();
      } catch (err) {
        showError(err.message);
      }
    }

    resetBtn.onclick = resetItems;
  </script>
</body>
</html>
