const response = $input.first().json.output || '';

// Extract matches using regex
const catMatch = response.match(/#cat_status:\s*(.+?)\s*\|\s*(.+)/);
const worldMatch = response.match(/#world_event:\s*(.+)/);

// Pull prior data from DB step
const previous = $items('Postgres')[0]?.json || {};
const current_world_history = previous.world_history || '';
const previous_location = previous.cat_location || '';
const previous_activity = previous.cat_activity || '';

// Extract and sanitize world event
const new_world_event = worldMatch ? worldMatch[1].trim() : null;

// Simple function to escape quotes for SQL safety (only needed if using raw SQL)
const escapeQuotes = (str) => str ? str.replace(/'/g, "''") : '';

// Build updated history safely
const updated_world_history = new_world_event
  ? `${current_world_history}\n${new_world_event}`.trim()
  : current_world_history.trim();

// Return safe values for update
return {
  cat_location: catMatch ? catMatch[1].trim() : previous_location,
  cat_activity: catMatch ? catMatch[2].trim() : previous_activity,
  new_world_event,
  current_world_history,
  updated_world_history: escapeQuotes(updated_world_history),
};

const text = $('AI Agent').first().json.output;

const cleaned = text
  .split('\n')
  .filter(line => !line.trim().startsWith('#'))
  .map(line => line.replace(/#[^#\n]+(?=\n|$)/g, '').trim())
  .join('\n')
  .trim();

return {
  output: cleaned
};
