import re
import json

def extract_urls_from_nested_json(data):
    """Recursively extract text from nested JSON structure"""
    urls = []

    def extract_text_recursive(obj):
        if isinstance(obj, dict):
            for key, value in obj.items():
                if key == 'text' and isinstance(value, str):
                    return value
                elif isinstance(value, (dict, list)):
                    result = extract_text_recursive(value)
                    if result:
                        return result
        elif isinstance(obj, list):
            for item in obj:
                result = extract_text_recursive(item)
                if result:
                    return result
        return None

    return extract_text_recursive(data)

# Get input from previous node
input_data = items[0]['json']

# Try multiple paths to extract text
text_to_search = ""
possible_paths = [
    # Primary path
    lambda x: x.get("candidates", [{}])[0].get("content", {}).get("parts", [{}])[0].get("text", ""),
    # Alternative paths
    lambda x: x.get("text", ""),
    lambda x: x.get("content", ""),
    # Recursive search
    lambda x: extract_urls_from_nested_json(x)
]

for path_func in possible_paths:
    try:
        result = path_func(input_data)
        if result and isinstance(result, str) and len(result.strip()) > 0:
            text_to_search = result
            break
    except (KeyError, TypeError, IndexError, AttributeError):
        continue

# Enhanced URL regex patterns
url_patterns = [
    # Standard HTTP/HTTPS URLs
    r'https?://[^\s<>"{}|\\^`\[\]]+',
    # URLs without protocol
    r'(?:www\.)?[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*\.[a-zA-Z]{2,}(?:/[^\s<>"{}|\\^`\[\]]*)?',
    # Domain-only patterns
    r'[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*\.[a-zA-Z]{2,}(?:/[^\s<>"{}|\\^`\[\]]*)?'
]

all_urls = []
for pattern in url_patterns:
    urls = re.findall(pattern, text_to_search, re.IGNORECASE)
    all_urls.extend(urls)

# Remove duplicates and filter valid URLs
unique_urls = []
seen = set()
for url in all_urls:
    # Clean up URL
    url = url.strip().rstrip('.,;:!?)')

    # Skip if already seen
    if url in seen:
        continue
    seen.add(url)

    # Add protocol if missing
    if not url.startswith(('http://', 'https://')):
        # Check if it looks like a valid domain
        if '.' in url and not url.startswith('www.'):
            url = 'https://' + url
        elif url.startswith('www.'):
            url = 'https://' + url
        else:
            continue

    # Basic validation
    if len(url) > 10 and '.' in url:
        unique_urls.append(url)

# Log extraction results
print(f"Extracted text length: {len(text_to_search)}")
print(f"Found {len(unique_urls)} unique URLs")

# Return results
if unique_urls:
    # Return each URL as a separate item for iteration
    return [{'json': {'url': url, 'extraction_success': True}} for url in unique_urls]
else:
    # Return a single item indicating no URLs found
    return [{'json': {'url': '', 'extraction_success': False, 'message': 'No URLs found in text'}}]

# Filter out items with "invalid data" summary
input_items = items if items else []

# Filter the data array to remove items with "invalid data" summary
filtered_data = []
for item in input_items:
    data_array = item.get('json', {}).get('data', [])
    filtered_array = []

    for data_item in data_array:
        output = data_item.get('output', {})
        if output.get('summary') != "invalid data":
            filtered_array.append(data_item)

    # Create new item with filtered data
    filtered_item = {
        'json': {
            'data': filtered_array
        }
    }
    if 'pairedItem' in item:
        filtered_item['pairedItem'] = item['pairedItem']

    filtered_data.append(filtered_item)

return filtered_data

