const input = $json["text"]?.toLowerCase().trim() || "";
const chatId = $json["chat_id"]; // you must ensure chat_id is passed in

if ((input.startsWith("create story"))) {
  const parts = input.split(" ");
  const task = parts.slice(2).join(" ").trim();
  return [{
    json: {
      task,
      type: parts[1],
      chat_id: chatId,
      should_respond: true,
      form_prompt: `Let's create a new ${parts[1]}.\n\nPlease answer the following:\n\nTitle:\nSummary:\nDue date (YYYY-MM-DD):\nDuration (in hours):`,
      original_message: $json["text"] || ""
    }
  }];
}

return [{
  json: {
    chat_id: chatId,
    original_message: $json["text"] || "",
    should_respond: false
  }
}];
