let results = [];

for (let item of items) {
    if (item.binary) {
        // If there's binary data in the item, process each binary file
        for (let key in item.binary) {
            // Use the key as the file name
            let binaryKey = key.replace(/\s/g, '_'); // Replace spaces with underscores for the key
            results.push({
                json: {
                    fileName: binaryKey
                },
                binary: {
                    [binaryKey]: item.binary[key] // Use the modified key for the binary data
                }
            });
        }
    }
}

return results;

let plainText = '' // we send plain text from all the PDFs to InfraNodus for analysis

const randomNum = Math.floor(Math.random() * 3); // replace this with a 0 if you'd like to address the biggest gap in the knowledge graph

for (let item of items) {
   plainText += item.json.text + '\n\n'  
}


return {text: plainText, randomNum};
