const raw = query;

if (!raw) {
  return 'N/A';
}

if (raw === '') {
  return 'N/A';
}

const secs = Number(raw);
const isTimestamp = Number.isFinite(secs) && /^[+-]?\d{8,13}$/.test(String(raw));

if (!isTimestamp) {
  return 'N/A';
}

const ms = secs * 1000;

const formatted = DateTime
  .fromMillis(ms, { zone: 'UTC' })
  .setZone('Europe/Berlin')
  .toFormat('dd.MM.yyyy HH:mm:ss ZZZ');

return String(formatted);

const raw = query || 'N/A';

const formats = [
  'd.LL.yyyy HH:mm:ss',
  'd.LL.yyyy',           
  'd LLLL yyyy HH:mm:ss',
  'd LLLL yyyy',  
  'd LLL yyyy',
  'yyyy-MM-dd',
  'LLLL d, yyyy',
  'd-M-yyyy',
];

let dt = null;
for (const fmt of formats) {
  dt = DateTime.fromFormat(raw, fmt, {
    zone  : 'Europe/Berlin',
    locale: 'en',
  });
  if (dt.isValid) break;
}

if (!dt?.isValid) {
  dt = DateTime.fromISO(raw, { zone: 'Europe/Berlin' });
}

if (!dt?.isValid) {
  dt = DateTime.fromSQL?.(raw, { zone: 'Europe/Berlin' });
}

if (!dt?.isValid) {
  return 'N/A';
}

const timestamp = Math.floor(dt.toUTC().toSeconds());

return timestamp;
